import Vector3 from "./math/Vector3";
import RigidBody from "./RigidBody";

class Bullets {
    constructor (bulletModel, bulletSpeed, bulletReach) {
        this.activeBullets = [];
        this.model = bulletModel;
        this.speed = bulletSpeed;
        this.reach = bulletReach;

        this.gravityIsEnabled = false;
        this.gravity = 9.82;

        this.rigidBodies = [];
    }

    addBullet(origin, direction) {
        this.activeBullets.push({
            origin: new Vector3(origin),
            position: origin,
            direction: direction,
            time: 0.0
        });

        let rb = new RigidBody([origin.x, origin.y, origin.z]);
        rb.setVelocity([direction.x * this.speed, direction.y * this.speed, direction.z * this.speed]);
        this.rigidBodies.push(rb);
    }

    calculateProjectileMotion(timeStep) {
        /*
        let i = 0;
        while(i < this.activeBullets.length) {
            let t = this.activeBullets[i].time;
            let x = this.activeBullets[i].origin.x + (this.activeBullets[i].direction.x * this.speed * t);
            let y = this.activeBullets[i].origin.y + (this.activeBullets[i].direction.y * this.speed * t);
            let z = this.activeBullets[i].origin.z + (this.activeBullets[i].direction.z * this.speed * t);

            // Add gravity if it's enabled
            if (this.gravityIsEnabled) {
                y -= (0.5 * this.gravity * t * t);
            }

            if (Vector3.distance(new Vector3([x, y, z]), this.activeBullets[i].origin) < this.reach) {
                // Save current position
                this.activeBullets[i].position = [x, y, z];
                this.activeBullets[i].time += timeStep;
                ++i;
            }
            else { // Remove bullet if distance is too great
                this.activeBullets.splice(i, 1);
            }
        }
        */
        this.rigidBodies.forEach((rb) => {
            rb.update(timeStep);
        })
    }

    enableGravity() {
        this.gravityIsEnabled = true;
    }

    disableGravity() {
        this.gravityIsEnabled = false;
    }

    update(timeStep) {
        this.calculateProjectileMotion(timeStep);
    }

    render(shader) {
        this.activeBullets.forEach((bulletInfo) => {
            // Set bullet position
            this.model.setPosition(bulletInfo.position);
            // Draw bullet
            this.model.render(shader);
        });
    }
}

export default Bullets;
