import Vector3 from "./math/Vector3";

class RigidBody {
    constructor(position, mass) {
        this.position = new Vector3(position);
        this.velocity = new Vector3([0.0, 0.0, 0.0]);
        this.acceleration = new Vector3([0.0, -9.82, 0.0]);
        console.log(this.acceleration);
        this.mass = mass;
        this.step = 0.0005;
    }

    update() {
        let nextVelocity = [
            this.velocity.x + this.acceleration.x * this.step,
            this.velocity.y + this.acceleration.y * this.step,
            this.velocity.z + this.acceleration.z * this.step,
        ];
        let nextPosition = new Vector3([
            this.position.x + this.velocity.x * this.step,
            this.position.y + this.velocity.y * this.step,
            this.position.z + this.velocity.z * this.step,
        ]);

        this.velocity.set(nextVelocity);
        this.position.set(nextPosition);
    }
}

export default RigidBody;
