// Enum struct for handling mouse events
const MouseKey = {
    LEFT: 1,
    MIDDLE: 2,
    RIGHT: 3
};

class EventHandler {
    constructor(sceneObjects) {

        this.mouseEvent = {
            movementX: 0,
            movementY: 0,
            deltaX: 0,
            deltaY: 0,
            isPressed: {
                leftButton: false,
                rightButton: false,
                middleButton: false
            },
            wheelDelta: 0
        };

        let me = this.mouseEvent;

        this.mouseInputReader = {
            leftClickIsActive: false,
            rightClickIsActive: false,
            moveX: 0,
            moveY: 0,
            altKey: false,
            ctrlKey: false,
            shiftKey: false,
            selectedGameObjectIndex: 0,
            selectedGameObjectAxis: null,
            moveGameObjectX: 0,
            moveGameObjectY: 0,
            wheelDelta: 0
        };
        let mouseInput = this.mouseInputReader;

        let canvas2D = document.getElementById('text-canvas');

        // Mouse down event function definition
        canvas2D.onmousedown = function(event) {

            if(event.which === MouseKey.LEFT) {
                me.isPressed.leftButton = true;
            }
            if (event.which === MouseKey.RIGHT) {
                me.isPressed.rightButton = true;
            }
            if (event.which === MouseKey.MIDDLE) {
                me.isPressed.middleButton = true;
            }

            switch(event.which) {
                case MouseKey.LEFT:
                    mouseInput.leftClickIsActive = true;
                    mouseInput.altKey = event.altKey;
                    mouseInput.ctrlKey = event.ctrlKey;
                    mouseInput.shiftKey = event.shiftKey;

                    // Extend the click box borders
                    let offset = 4;

                    for (let i = 0; i < sceneObjects.length; ++i) {
                        // Check if the coordinate system is visible
                        if (sceneObjects[i].localCoordinateSystemUI.isVisible) {
                            // Check if mouse is in the click box area
                            if (event.offsetX <= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.xMax+offset &&
                                event.offsetX >= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.xMin-offset &&
                                event.offsetY <= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.yMax+offset &&
                                event.offsetY >= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.yMin-offset) {

                                mouseInput.selectedGameObjectAxis = 'x';
                            }
                            else if (event.offsetX <= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMax+offset &&
                                event.offsetX >= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMin-offset &&
                                event.offsetY <= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.yMax+offset &&
                                event.offsetY >= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.yMin-offset) {

                                mouseInput.selectedGameObjectAxis = 'y';
                            }
                            else if (event.offsetX <= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMax+offset &&
                                event.offsetX >= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisZ.borders.xMin-offset &&
                                event.offsetY <= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisZ.borders.yMax+offset &&
                                event.offsetY >= sceneObjects[i].localCoordinateSystemUI.clickBoxAxisZ.borders.yMin-offset) {

                                mouseInput.selectedGameObjectAxis = 'z';
                            }

                            mouseInput.selectedGameObjectIndex = i;
                        }
                    }
                    break;
                case MouseKey.RIGHT:
                    mouseInput.rightClickIsActive = true;
                    break;
                default:
                    break;
            }
        };

        // Mouse up event function definition
        canvas2D.onmouseup = function(event) {
            if(event.which === MouseKey.LEFT) {
                me.isPressed.leftButton = false;
            }
            if (event.which === MouseKey.RIGHT) {
                me.isPressed.rightButton = false;
            }
            if (event.which === MouseKey.MIDDLE) {

            }

            switch(event.which) {
                case MouseKey.LEFT:
                    mouseInput.leftClickIsActive = false;
                    mouseInput.selectedGameObjectAxis = null;
                    break;
                case MouseKey.RIGHT:
                    mouseInput.rightClickIsActive = false;
                    break;
                default:
                    break;
            }
        };

        // Mouse leave event function definition
        canvas2D.onmouseleave = function(event) {
            if(event.which === MouseKey.LEFT) {
                me.isPressed.leftButton = false;
            }
            if (event.which === MouseKey.RIGHT) {
                me.isPressed.rightButton = false;
            }

            if (mouseInput.leftClickIsActive) {
                mouseInput.leftClickIsActive = false;
            }
            else if (mouseInput.rightClickIsActive) {
                mouseInput.rightClickIsActive = false;
            }
        };

        // Mouse move event function definition
        canvas2D.onmousemove  = function(event) {

            me.movementX = event.movementX;
            me.movementY = event.movementY;

            if (mouseInput.leftClickIsActive || mouseInput.rightClickIsActive) {
                mouseInput.moveX = event.movementX;
                mouseInput.moveY = event.movementY;
                mouseInput.moveGameObjectX = event.movementX;
                mouseInput.moveGameObjectY = event.movementY;
            }
        };

        // Mouse wheel event function definition
        canvas2D.onwheel = function(event) {
            me.wheelDelta = event.deltaY;

            mouseInput.wheelDelta = event.deltaY;
        };
    }

    processCameraMovement (camera) {
        // Zoom camera view in and out
        if (this.mouseEvent.isPressed.wheelButton) {
            camera.translate([0.0, 0.0, -(this.mouseEvent.movementY+this.mouseEvent.movementX)/4]);
            this.mouseEvent.movementX = 0;
            this.mouseEvent.movementY = 0;
        } // Pan the camera view
        else if(this.mouseEvent.isPressed.leftButton) {
            camera.translate([-this.mouseEvent.movementX/4, this.mouseEvent.movementY/4, 0.0]);
            this.mouseEvent.movementX = 0;
            this.mouseEvent.movementY = 0;
        } // Rotate the camera view
        else if (this.mouseEvent.isPressed.rightButton) {
            camera.rotate([-this.mouseEvent.movementY/8, -this.mouseEvent.movementX/8, 0.0]);
            this.mouseEvent.movementX = 0;
            this.mouseEvent.movementY = 0;
        }

        if (Math.abs(this.mouseEvent.wheelDelta) > 0.01) {
            if (this.mouseEvent.wheelDelta > 0.0) {
                camera.translate([0.0, 0.0, 4.0]);
            }
            else
                camera.translate([0.0, 0.0, -4.0]);

            this.mouseEvent.wheelDelta = 0;
        }

