import Time from "./Time";

class Scene {
    constructor() {
        this.sceneObjects = [];
        this.lightSources = [];
    }

    addGameObject(gameObject) {
        this.sceneObjects.push(gameObject);
    }

    addLightSource(lightSource) {
        this.lightSources.push(lightSource);
        this.lightSourceOffsetX = lightSource.transform.position.x;
    }

    draw() {
        this.sceneObjects.forEach((object)=> {
            object.render();
        });

        this.lightSources.forEach((lightSource) => {
            let currentPos = lightSource.transform.position;
            currentPos.x = this.lightSourceOffsetX + Math.sin(Time.time * 0.001);
            lightSource.setPosition([currentPos.x, currentPos.y, currentPos.z]);
            lightSource.render();
        });
    }
}

export default Scene;
