import Transform from './Transform';
import Vector3 from "./math/Vector3";
import Raycast from "./Raycast";

// TODO: Linear interpolation for transforms (translation and rotation)
// TODO: Implement visible and interactable local coordinate system for handling game objects in the editor
// TODO: Implement a RigidBody component for handling velocity, acceleration and mass
// TODO: Implement a Collider component for handling collision between game objects
// TODO: Implement a Material component for handling color, textures, etc.
// TODO: Implement a Script component for game object logic

class GameObject {
    constructor (name) {
        this.nameTag = name;
        this.meshes = [];
        this.transform = new Transform();
        this.collider = null;
        this.rigidBody = null;
        this.isActive = false;
    }

    get worldPosition() {
        return new Vector3([this.transform.matrix[12], this.transform.matrix[13], this.transform.matrix[14]]);
    }

    setNameTag(name) {
        this.nameTag = name;
    }

    translate (val) {
        this.transform.translate(val);
    }

    rotate(rot) {
        this.transform.rotate(rot);
    }

    setPosition(pos) {
        this.transform.setPosition(pos);
    }

    setRotation(rot) {
        this.transform.setRotation(rot);
    }

    setScale(scale) {
        this.transform.setScale(scale);
    }

    setActive(state) {
        this.isActive = state;

        if (this.collider !== null) {
            this.collider.setVisible(state);
        }
    }

    addMesh (mesh) {
        this.transform.addChild(mesh.transform);
        this.meshes.push(mesh);
    }

    addCollider (collider) {
        this.transform.addChild(collider.transform);
        this.collider = collider;
    }

    addRigidBody (rigidBody) {
        this.rigidBody = rigidBody;
    }

    render(shader) {
        for (let i = 0; i < this.meshes.length; ++i) {
            this.meshes[i].render(shader);
        }
    }

    update(timeStep) {
        if (this.rigidBody) {

            if (this.rigidBody.velocity.magnitude < 1.0)
                return;
            
            this.rigidBody.update(timeStep);
            this.transform.setPosition([this.rigidBody.position.x, this.rigidBody.position.y, this.rigidBody.position.z]);

            if (this.collider) {
                let plane = {
                    normal: new Vector3([0.0, 1.0, 0.0]),
                    offset: 0.0
                };

                let rayDir = this.rigidBody.velocity.normalized();
                let rayOrigin = this.rigidBody.position.clone();

                let intersectionPoint = Raycast.planeIntersectionTest(rayDir, rayOrigin, plane);
                if (intersectionPoint !== null) {
                    if (Vector3.distance(intersectionPoint, this.collider.worldPosition) < this.collider.radius*2.0) {

                        let reflectDirection = Vector3.subtract(plane.normal.clone().multipleBy(2.0 * Vector3.dot(plane.normal, rayDir)), rayDir);
                        let bounce = -this.rigidBody.velocity.magnitude*0.75;

                        this.rigidBody.setVelocity([
                            reflectDirection.x*bounce,
                            reflectDirection.y*bounce,
                            reflectDirection.z*bounce
                        ]);
                    }
                }
            }
            else {
                this.rigidBody.update(timeStep);
                this.transform.setPosition([this.rigidBody.position.x, this.rigidBody.position.y, this.rigidBody.position.z]);
            }
        }

        this.customUpdate(timeStep);
    }

    customUpdate(timeStep) {

    }
}

export default GameObject;
