import React, { Component } from 'react';
import Shader from './Shader.js';
import Time from './Time.js';

class Engine extends Component {
    constructor(props) {
        super(props);

        // Initialize timing variables
        this.fpsTimer = 0;
        this.frameCount = 0;
        this.fps = 0;
        this.animationId = null;
    }

    componentDidMount() {
        this.preload();
        this.startMainLoop();
    }

    componentWillUnmount() {
        this.stopMainLoop();
    }

    displayFrameRate() {

        this.tNow = Time.time;

        // If tNow - tLast >= 1000, one second has passed
        if ( (this.tNow - this.fpsTimer) >= 1000) {
            // Fps = the number of frames
            this.fps = this.frameCount;
            // Update timer
            this.fpsTimer = this.tNow;
            // Reset frame count
            this.frameCount = 0;
            // Clear previous
            this.ctx.clearRect(0, 0, this.props.width, 25);
            // Draw fps and frame time in milliseconds
            this.ctx.fillText("Frame rate: " + this.fps + " fps", 0, 15);
        }
        else {
            ++this.frameCount;
        }
    }

    preload() {
        // 2D Canvas for text display
        this.canvas2D = document.getElementById('text-canvas');
        this.ctx = this.canvas2D.getContext('2d');
        this.ctx.font = "20px Consolas";
        this.ctx.fillStyle = 'white';

        // WebGL canvas
        this.canvas = document.getElementById("gl-canvas");
        // Initialize the WebGL context
        this.gl = this.canvas.getContext("webgl");

        // If WebGL isn't supported
        if (!this.gl) {
            console.log("WebGL is not supported");
            alert("Unable to initialize WebGL. Your browser or machine may not support it.");
            return 0;
        }

        this.gl.viewport(0, 0, this.canvas.width, this.canvas.height);
        // Set clear color to light blue, fully opaque
        this.gl.clearColor(0.0, 0.40, 0.55, 1.0);
        // Enable depth so that triangles closer to the camera don't get overlapped by those further away
        this.gl.enable(this.gl.DEPTH_TEST);
        // Only draw "visible" sides (memory saving)
        this.gl.enable(this.gl.CULL_FACE);
        this.gl.cullFace(this.gl.BACK);
        this.gl.frontFace(this.gl.CCW);

        this.mainShader = new Shader(this.canvas);

        //this.SetUpEventListeners();
    }

    startMainLoop() {
        // Initiate the main loop
        this.animationId = window.requestAnimationFrame( this.mainLoop.bind(this) );
    }

    stopMainLoop() {
        window.cancelAnimationFrame( this.animationId );
        this.animationId = null;
    }

    mainLoop() {
        this.animationId = window.requestAnimationFrame( this.mainLoop.bind(this) );

        // Keeps track of time passed and delta time
        Time.Update();

        // Clear screen 3D
        this.gl.clear(this.gl.DEPTH_BUFFER_BIT | this.gl.COLOR_BUFFER_BIT);
        // Clear screen 2D
        this.ctx.clearRect(0, 25, this.props.width, this.props.height);

        // Draw objects

        // Clear depth so that local coordinate axes is visible through models

        // Draw local coordinate axes UI

        // Calculate fps
        this.displayFrameRate();
    }

    render() {
        const engine_container_style = {
            display: 'flex',
            flexDirection: 'column',
            width: this.props.width
        };

        return (
            <div className="Engine-container" style={engine_container_style}>
                <div>
                    <p>
                        TADA
                    </p>
                </div>
                
                <div style={{display: 'flex', flexDirection: 'row'}}>
                    <div style={{width: this.props.width, height: this.props.height}}>
                        <canvas id='gl-canvas' width={this.props.width} height={this.props.height}>
                            Your browser does not support HTML5
                        </canvas>
                        <canvas id="text-canvas" width={this.props.width} height={this.props.height}>
                        </canvas>
                    </div>
                    {this.DisplayPanel()}

                </div>
            </div>
        );
    }
}

export default Engine;
