        // Create a magenta cylinder
        this.createGameObject({
            shape: SHAPE.CYLINDER,
            name: 'Magenta Cylinder',
            scale: [10.0, 10.0, 10.0],
            position: [-40.0, 20.0, 20.0],
            color: [1.0, 0.0, 1.0]
        });

        // Create gray floor
        this.createGameObject({
            shape: SHAPE.CUBE,
            name: 'Floor',
            scale: [200.0, 1.0, 200.0],
            position: [0.0, 0, 0, 0],
            color: [0.5, 0.5, 0.5]
        });

        // Create Walls
        this.createGameObject({
            shape: SHAPE.CUBE,
            name: 'Wall 01',
            scale: [1.0, 30.0, 200.0],
            position: [-200.0, 30.0, 0, 0],
            color: [0.5, 0.5, 0.5]
        });
        this.createGameObject({
            shape: SHAPE.CUBE,
            name: 'Wall 02',
            scale: [1.0, 30.0, 200.0],
            position: [200.0, 30.0, 0, 0],
            color: [0.5, 0.5, 0.5]
        });
        this.createGameObject({
            shape: SHAPE.CUBE,
            name: 'Wall 03',
            scale: [200.0, 30.0, 1.0],
            position: [0.0, 30.0, -200, 0],
            color: [0.5, 0.5, 0.5]
        });
    }

