                                event.offsetX >= gameObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.xMin-offset &&
                                event.offsetY <= gameObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.yMax+offset &&
                                event.offsetY >= gameObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.yMin-offset) {

                                mouseInput.selectedGameObjectAxis = 'x';
                            }
                            else if (event.offsetX <= gameObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMax+offset &&
                                event.offsetX >= gameObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMin-offset &&
                                event.offsetY <= gameObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.yMax+offset &&
                                event.offsetY >= gameObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.yMin-offset) {

