// JavaScript
import Time from "./Time";
import GraphicsEngine from "./GraphicsEngine";

class Engine {
    constructor() {
        this.graphicsEngine = new GraphicsEngine();
    }

    /*
    * @brief This is the engines game loop and is called every iteration of the main loop
    */
    run() {
        // Keeps track of time passed and delta time
        Time.update();

        // Draw all graphics
        this.graphicsEngine.render();
    }

    preload() {
        // Set up webgl context
        this.graphicsEngine.preload();
    }
}

export default Engine;
