      this.ctx.fillStyle = 'white'; // Fps = the number of frames

      this.fps = this.frameCount; // Update timer

      this.lastTime = currentTime; // Reset frame count

      this.frameCount = 0; // Clear previous

      this.ctx.clearRect(0, 0, this.canvas.width, 30); // Draw fps and frame time in milliseconds

      this.ctx.fillText("Frame rate: " + this.fps + " fps", 10, 25);
    } else {
      ++this.frameCount;
    }
  }

