// JavaScript components
import Shader from "./ShaderHandle";
import Time from "./Time";
import Camera from "./Camera";
import Mesh from "./Mesh";
import GameObject from "./GameObject";
import EventHandler from "./EventHandler";
import Scene from "./Scene";
import PointLightSource from "./PointLightSource";

// Shader files
import PhongLightingShader from "./shaders/PhongLightingShader";
import AmbientShader from "./shaders/AmbientShader";

/***** Settings *****/
// Default camera position and rotation
const CAMERA_POSITION = [0.0, 100.0, 500.0];
const CAMERA_ROTATION = [-15.0, 0.0, 0.0];

// Enum struct for creating different shapes
const SHAPE = {
    CUBE: 0,
    SPHERE: 1,
    CYLINDER: 2,
    PLANE: 3
};

class GraphicsEngine {
    constructor() {
        // Initialize timing variables
        this.lastTime = 0.0;
        this.frameCount = 0;
        this.fps = 0;
        this.currentTextOffset = 0;
        this.scenes = [];
        this.currentScene = null;
        this.shaderLits = new Map();
    }

    /*
    * @brief This is the engines main update function and is called every iteration of the main loop
    */
    run() {
        // Set the viewport to the canvas dimensions and update the aspect ratio for the projection matrix
        this.updateViewportDimensions();

        // Handle camera navigation
        this.eventHandler.processCameraNavigation(this.camera);

        // Clear screen 3D
        this.gl.clear(this.gl.DEPTH_BUFFER_BIT | this.gl.COLOR_BUFFER_BIT);

        // Draw the 3D scene
        this.currentScene.draw();

        // Display fps and other information
        this.drawTextElements();
    }

    updateViewportDimensions() {
        // Calculate current aspect ratio
        let aspectRatio = this.canvas.width / this.canvas.height;

        // Update the projection matrix
        this.mainShader.activate();
        this.mainShader.setProjectionUniform(aspectRatio);

        this.ambientShader.activate();
        this.ambientShader.setProjectionUniform(aspectRatio);

        // Update the viewport dimensions to the canvas width
        this.gl.viewport(0, 0, this.canvas.width, this.canvas.height);
    }

    preload() {
        // Initiate the mouse event handler
        this.eventHandler = new EventHandler();

        // 2D Canvas for text display
        this.canvas2D = document.getElementById('text-canvas');
        this.ctx = this.canvas2D.getContext('2d');

        // WebGL canvas
        this.canvas = document.getElementById("gl-canvas");
        // Initialize the WebGL context
        this.gl = this.canvas.getContext("webgl");

        // If WebGL isn't supported
        if (!this.gl) {
            console.log("WebGL is not supported");
            alert("Unable to initialize WebGL. Your browser or machine may not support it.");
            return 0;
        }

        // Set the viewport dimensions to the same as the canvas
        this.gl.viewport(0, 0, this.canvas.width, this.canvas.height);
        // Set clear color to dark blue, fully opaque
        this.gl.clearColor(0.02, 0.02, 0.02, 1.0);
        // Enable depth so that triangles closer to the camera don't get overlapped by those further away
        this.gl.enable(this.gl.DEPTH_TEST);
        // Only draw "visible" sides (memory saving)
        this.gl.enable(this.gl.CULL_FACE);
        this.gl.cullFace(this.gl.BACK);
        this.gl.frontFace(this.gl.CCW);

        // Create shaders
        this.mainShader = new Shader(this.canvas, PhongLightingShader.vertex, PhongLightingShader.fragment);
        this.ambientShader = new Shader(this.canvas, AmbientShader.vertex, AmbientShader.fragment);

        // Initialize the main camera
        this.camera = new Camera([this.mainShader, this.ambientShader]);
        this.camera.setPosition(CAMERA_POSITION);
        this.camera.setRotation(CAMERA_ROTATION);

        // Create a scene for the objects
        this.currentScene = new Scene();

        // Create scene objects
        this.loadDefaultSceneObjects();
    }

    drawTextElements() {
        // Set text font style
        this.ctx.font = "20px Consolas";
        this.ctx.fillStyle = 'white';
        this.currentTextOffset = 0;

        // Clear previous
        this.ctx.clearRect(this.currentTextOffset, 0, this.canvas.offsetWidth, 30);

        // Draw the text
        this.displayFrameRate();
        this.displayCameraPosition();
    }

    displayFrameRate() {
        let currentTime = Time.time;
        // If tNow - tLast >= 1000, one second has passed
        if ((currentTime - this.lastTime) >= 1000.0) {
            // Fps = the number of frames
            this.fps = this.frameCount;
            // Update timer
            this.lastTime = currentTime;
            // Reset frame count
            this.frameCount = 0;
        }
        else {
            ++this.frameCount;
        }

        // Compose text string
        let fpsText = "Frame rate: " + this.fps + " fps";
        // Draw fps and frame time in milliseconds
        this.ctx.fillText(fpsText, 10, 25);
        // Update text offset for next text
        this.currentTextOffset = fpsText.length * 12 + 50;
    }

    displayCameraPosition() {
        let pos = this.camera.transform.position;
        // Compose text string
        let cameraPositionText = "Camera position: (" + pos.x.toFixed(2) + ", " + pos.y.toFixed(2) + ", " + pos.z.toFixed(2) + ")";

        // Draw fps and frame time in milliseconds
        this.ctx.fillText(cameraPositionText, this.currentTextOffset, 25);
        // Update text offset for next text
        this.currentTextOffset = cameraPositionText.length * 12;
    }

    createGameObject (settings) {
        let mesh = new Mesh(this.mainShader);
        if (settings.shape === SHAPE.CUBE) {
            mesh.createCube();
        }
        else if (settings.shape === SHAPE.SPHERE) {
            mesh.createSphere(32);
        }
        else if (settings.shape === SHAPE.CYLINDER) {
            mesh.createCylinder(32,1);
        }
        else if (settings.shape === SHAPE.PLANE) {
            mesh.createPlane(50,50);
        }
        mesh.setColor(settings.color);

        let obj = new GameObject (settings.name);
        obj.setPosition(settings.position);
        obj.setScale(settings.scale);
        obj.addMesh(mesh);

        return obj;
    }

    loadDefaultSceneObjects() {
        let lightColor = [1.0, 1.0, 1.0];
        // Create a light source
        let lightSource = new PointLightSource("Lamp", this.mainShader);
        lightSource.setPosition([150.0, 75.0, -150.0]);
        lightSource.setScale([5.0, 5.0, 5.0]);
        lightSource.setColor(lightColor);

        // Create a model for the light source
        let lightSourceModel = new Mesh(this.ambientShader);
        lightSourceModel.createSphere(8);
        lightSourceModel.setColor(lightColor);
        lightSource.addMesh(lightSourceModel);

        // Add the light source to our scene
        this.currentScene.addLightSource(lightSource);

        // Create a red cube
        this.currentScene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Red Cube',
                scale: [10.0, 10.0, 10.0],
                position: [0.0, 10.0, 0.0],
                color: [1.0, 0.0, 0.0]
            })
        );

        // Create a green sphere
        this.currentScene.addGameObject(
            this.createGameObject({
                shape: SHAPE.SPHERE,
                name: 'Green Sphere',
                scale: [10.0, 10.0, 10.0],
                position: [40.0, 20.0, 20.0],
                color: [0.0, 1.0, 0.0]
            })
        );

        // Create a magenta cylinder
        this.currentScene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CYLINDER,
                name: 'Magenta Cylinder',
                scale: [10.0, 10.0, 10.0],
                position: [-40.0, 20.0, 20.0],
                color: [1.0, 0.0, 1.0]
            })
        );

        // Create gray floor
        this.currentScene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Floor',
                scale: [200.0, 1.0, 200.0],
                position: [0.0, 100.0, 0.0],
                color: [0.5, 0.5, 0.5]
            })
        );

        // Create gray roof
        this.currentScene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Roof',
                scale: [200.0, 1.0, 200.0],
                position: [0.0, 0, 0],
                color: [0.5, 0.5, 0.5]
            })
        );

        // Create Walls
        let wallColor = [0.0, 0.2, 1.0];
        this.currentScene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Wall 01',
                scale: [1.0, 50.0, 200.0],
                position: [-200.0, 50.0, 0],
                color: wallColor
            })
        );
        this.currentScene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Wall 02',
                scale: [1.0, 50.0, 200.0],
                position: [200.0, 50.0, 0],
                color: wallColor
            })
        );
        this.currentScene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Wall 03',
                scale: [200.0, 50.0, 1.0],
                position: [0.0, 50.0, -200],
                color: wallColor
            })
        );
    }
}

export default GraphicsEngine;
