class MouseEvent {
    static init() {
        this.xPos = 0;
        this.yPos = 0;
        this.moveX = 0;
        this.moveY = 0;
        this.wheelDelta = 0;
        this.isPressed = {
            leftButton: false,
            rightButton: false,
            middleButton: false
        };
        
        this.down = false;
        this.up = false;
    }

    static set offsetX(offset) {
        this.xPos = offset;
    }
    static set mouseDownEvent(state) {
        this.mouseDown = state;
    }
    static get mouseDown() {
        return this.mouseDown;
    }
}

export default MouseEvent;
