        let verticalSegments = 2*horizontalSegments;

        let vertices = [];

        // Bottom vertex and its normal (x, y, z)
        vertices[0] = 0.0; vertices[1] = -1.0; vertices[2] = 0.0;
        vertices[3] = 0.0; vertices[4] = -1.0; vertices[5] = 0.0;
        vertices[6] = 0.0; vertices[7] = 0.0;

        let sampleResolution = Math.PI/horizontalSegments;
        let theta = -Math.PI + sampleResolution; // Go from bottom to top (Y € -PI < theta < PI )
        let phi = 0; // Begin at Z = 0 (Z € 0 < phi < 2PI )

