        let index = 7;
        for(let i = 0; i < horizontalSegments-1; ++i) {
            let y = Math.cos(theta);
            let R = Math.sin(theta); // radius

            for (let j = 0; j < verticalSegments+1; ++j) {
                // Vertex (x, y, z)
                vertices[++index] = R*Math.sin(phi);
                vertices[++index] = y;
                vertices[++index] = R*Math.cos(phi);
                // Normal (x, y, z)
                vertices[++index] = R*Math.sin(phi);
                vertices[++index] = y;
                vertices[++index] = R*Math.cos(phi);
                // Textures (s, t)
                vertices[++index] = 0.0;
                vertices[++index] = 0.0;

                phi += sampleResolution;
            }
            theta += sampleResolution;
        }

        // Top vertex and its normal (x, y, z)
        vertices[++index] = 0; vertices[++index] = 1; vertices[++index] = 0;
        vertices[++index] = 0; vertices[++index] = 1; vertices[++index] = 0;
        vertices[++index] = 0.0; vertices[++index] = 0.0;

        /* Generate Index Array */
        // Bottom cap
        let indices = [];
        index = -1;
        for (let i = 0; i < verticalSegments; ++i) {
            indices[++index] = 0;

            if( (i+2) <= verticalSegments)
                indices[++index] = i+2;
            else
                indices[++index] = (i+2)-verticalSegments;

            indices[++index] = i+1;
        }

        // Middle part
        let v0 = 1;
        for(let i = 0; i < horizontalSegments-2; i++) {
            for (let j = 0; j < verticalSegments; ++j) {
