            indices[++index] = v0;
            indices[++index] = (v0 + 1) - verticalSegments;
            indices[++index] = verticalSegments + v0;
            indices[++index] = (v0 + 1) - verticalSegments;
            indices[++index] = v0 + 1;
            indices[++index] = verticalSegments + v0;
            ++v0;
        }

        // Top cap
        let lastVertexIndex = (vertices.length/8)-1;
        for (let i = 0; i < verticalSegments; ++i) {

