        }

        // Middle part
        let v0 = verticalSegments+2;
        for(let i = 0; i < horizontalSegments; i++) {

            for (let j = 0; j < verticalSegments; ++j) {
                // One rectangle at a time (two triangles)
                indices[++index] = v0;
                indices[++index] = v0 + 1;
                indices[++index] = verticalSegments + v0;
                indices[++index] = v0 + 1;
                indices[++index] = verticalSegments + v0 + 1;
