import Transform from "./Transform";
import Vector3 from "./math/Vector3";

class SphereCollider {
    constructor() {
        this.transform = new Transform();
        this.sphereRadius = 1.2;

        this.isVisible = false;
    }

    get worldPosition() {
        return new Vector3([this.transform.matrix[12], this.transform.matrix[13], this.transform.matrix[14]]);
    }

    get radius () {
        if (this.transform.parent) {
            return this.transform.parent.scaling.x * this.sphereRadius;
        }

        return this.transform.scaling.x * this.sphereRadius;
    }

    setVisible(state) {
        this.isVisible = state;
    }
}

export default SphereCollider;
