import React, { Component } from 'react';

// CSS
import '../../../css/EngineWindow.css';

// JS
import Engine from '../Engine';

class EngineWindow extends Component {
    constructor(props) {
        super(props);

        this.state = {
            canvasWidth: this.props.width,
            canvasHeight: this.props.height
        };

        this.animationId = null;
        this.engine = new Engine();
    }

    updateWindowDimensions() {

        let widthOffset = document.getElementById("side-panel").width;
        this.setState({
            canvasWidth: this.canvas.offsetWidth - widthOffset,
            canvasHeight: this.canvas.offsetHeight
        });
    }

    componentDidMount() {
        this.engine.preload();
        this.startMainLoop();
        this.canvas = document.getElementById("gl-canvas");

        this.updateWindowDimensions();
        window.addEventListener("resize", this.updateWindowDimensions.bind(this));
    }

    componentWillUnmount() {
        this.stopMainLoop();
        window.removeEventListener("resize", this.updateWindowDimensions.bind(this));
    }

    startMainLoop() {
        // Initiate the main loop
        this.animationId = window.requestAnimationFrame( this.mainLoop.bind(this) );
    }

    stopMainLoop() {
        window.cancelAnimationFrame( this.animationId );
        this.animationId = null;
    }

    mainLoop() {
        this.animationId = window.requestAnimationFrame( this.mainLoop.bind(this) );

        this.engine.run();
    }

    render() {
        const engine_container_style = {
            display: 'flex',
            flexDirection: 'column',
            width: this.props.width
        };

        return (
            <div className="Engine-container" style={engine_container_style}>
                <canvas id='gl-canvas' width={this.state.canvasWidth} height={this.state.canvasHeight}>
                    Your browser does not support HTML5
                </canvas>
                <canvas id="text-canvas" width={this.state.canvasWidth} height={this.state.canvasHeight}>
                </canvas>
            </div>
        );
    }
}

export default EngineWindow;
