import Transform from "./Transform";
import Vector3 from "./math/Vector3";

class Bullets {
    constructor (bulletModel, bulletSpeed, bulletReach) {
        this.activeBullets = [];
        this.model = bulletModel;
        this.speed = bulletSpeed;
        this.reach = bulletReach;
    }

    addBullet(origin, direction) {
        this.activeBullets.push({
            origin: new Vector3(origin),
            position: origin,
            direction: direction,
            time: 0.0
        });
    }

    update() {
        let bulletTransform = new Transform();
        let i = 0;
        while(i < this.activeBullets.length) {

            let v0 = this.speed;
            let t = this.activeBullets[i].time;
            let g = -9.82;
            let x = this.activeBullets[i].origin.x + v0 * t;
            let y = this.activeBullets[i].origin.y + v0 * t - (0.5*g*t);
            let z = this.activeBullets[i].origin.z + v0 * t;

            // Set bullets position and move it in the bullet direction
            bulletTransform.setPosition(this.activeBullets[i].position);
            bulletTransform.translate([this.activeBullets[i].direction.x * this.speed, (this.activeBullets[i].direction.y * this.speed), this.activeBullets[i].direction.z * this.speed]);

            // Check distance from camera and bullet
            if (Vector3.distance(bulletTransform.position, this.activeBullets[i].origin) < this.reach) {
                // Save current position
                this.activeBullets[i].position = [bulletTransform.position.x, bulletTransform.position.y, bulletTransform.position.z];
                ++i;
            }
            else { // Remove bullet if distance is too great
                this.activeBullets.splice(i, 1);
            }
        }
    }

    render(shader) {
        this.activeBullets.forEach((bulletInfo) => {
            // Set bullet position
            this.model.setPosition(bulletInfo.position);
            // Draw bullet
            this.model.render(shader);
        });
    }
}

export default Bullets;
