// JS
import Shader from "./Shader";
import Time from "./Time";
import Camera from "./Camera";
import Mesh from "./Mesh";
import GameObject from "./GameObject";

// Settings
const CAMERA_POSITION = [0.0, 50.0, 300.0];
const CAMERA_ROTATION = [-15.0, 0.0, 0.0];
const SHAPE = {
    CUBE: 0,
    SPHERE: 1,
    CYLINDER: 2,
    PLANE: 3
};

class Engine {
    constructor() {
        // Initialize timing variables
        this.lastTime = 0;
        this.frameCount = 0;
        this.fps = 0;
    }

    run() {
        // Keeps track of time passed and delta time
        Time.Update();

        // Update the viewport dimensions to the canvas width
        this.gl.viewport(0, 0, this.canvas.width, this.canvas.height);

        // Draw the 3D scene
        this.drawScene();

        // Clear screen 2D
        //this.ctx.clearRect(0, 25, this.canvas.width, this.canvas.height);

        // Calculate fps
        this.displayFrameRate();
    }

    displayFrameRate() {
        let currentTime = Time.time;
        // If tNow - tLast >= 1000, one second has passed
        if ((currentTime - this.lastTime) >= 1000) {
            // Set text font style
            this.ctx.font = "20px Consolas";
            this.ctx.fillStyle = 'white';
            // Fps = the number of frames
            this.fps = this.frameCount;
            // Update timer
            this.lastTime = currentTime;
            // Reset frame count
            this.frameCount = 0;
            // Clear previous
            this.ctx.clearRect(0, 0, this.canvas.width, 25);
            // Draw fps and frame time in milliseconds
            this.ctx.fillText("Frame rate: " + this.fps + " fps", 0, 15);
        }
        else {
            ++this.frameCount;
        }
    }

    preload() {
        // 2D Canvas for text display
        this.canvas2D = document.getElementById('text-canvas');
        this.ctx = this.canvas2D.getContext('2d');

        // WebGL canvas
        this.canvas = document.getElementById("gl-canvas");
        // Initialize the WebGL context
        this.gl = this.canvas.getContext("webgl");

        // If WebGL isn't supported
        if (!this.gl) {
            console.log("WebGL is not supported");
            alert("Unable to initialize WebGL. Your browser or machine may not support it.");
            return 0;
        }

        this.gl.viewport(0, 0, this.canvas.width, this.canvas.height);
        // Set clear color to light blue, fully opaque
        this.gl.clearColor(0.0, 0.40, 0.55, 1.0);
        // Enable depth so that triangles closer to the camera don't get overlapped by those further away
        this.gl.enable(this.gl.DEPTH_TEST);
        // Only draw "visible" sides (memory saving)
        this.gl.enable(this.gl.CULL_FACE);
        this.gl.cullFace(this.gl.BACK);
        this.gl.frontFace(this.gl.CCW);

        this.mainShader = new Shader(this.canvas);

        this.camera = new Camera(this.gl, this.mainShader.cameraViewUniformLocation);
        this.camera.SetPosition(CAMERA_POSITION);
        this.camera.SetRotation(CAMERA_ROTATION);

        //this.SetUpEventListeners();
    }

    drawScene() {
        // Clear screen 3D
        this.gl.clear(this.gl.DEPTH_BUFFER_BIT | this.gl.COLOR_BUFFER_BIT);

        // Draw objects

        // Clear depth so that local coordinate axes is visible through models

        // Draw local coordinate axes UI
    }

    createGameObject (settings) {
        let mesh = new Mesh(this.mainShader);
        if (settings.shape === SHAPE.CUBE) {
            mesh.CreateCube();
        }
        else if (settings.shape === SHAPE.SPHERE) {
            mesh.CreateSphere(32);
        }
        else if (settings.shape === SHAPE.CYLINDER) {
            mesh.CreateCylinder(32,1);
        }
        else if (settings.shape === SHAPE.PLANE) {
            mesh.CreatePlane(50,50);
        }
        mesh.SetColor(settings.color);

        let obj = new GameObject (settings.name, this.mainShader);
        obj.SetPosition(settings.position);
        obj.SetScale(settings.scale);
        obj.AddMesh(mesh);

        let update = this.state.listOfGameObjects;
        update.push(obj);
        this.setState({
            listOfGameObjects: update
        });
    }
}

export default Engine;
