import Time from "./Time";

class Scene {
    constructor(shaders) {
        this.sceneObjects = [];
        this.lightSources = [];
        this.shaders = shaders;
    }

    addGameObject(gameObject) {
        this.sceneObjects.push(gameObject);
    }

    addLightSource(lightSource) {
        let shader = this.shaders.get("main");
        shader.activate();

        this.lightSources.push(lightSource);
        let lightPositions = [];
        let stride = 7; // 7 values per light source component, position(x,y,z) + color (r,g,b) + strength
        for (let i = 0; i < this.lightSources.length; ++i) {
            let lightSource = this.lightSources[i];

            let positionLoc = "pointLights[" + i + "]";
            shader.setUniformVec3f(shader.getUniformLoc(positionLoc), [lightSource.transform.position.x, lightSource.transform.position.y, lightSource.transform.position.z]);

            let colorLoc = "pointLights[" + i + "]";
            shader.setUniformVec3f(shader.getUniformLoc(colorLoc), [lightSource.color.x, lightSource.color.y, lightSource.color.z]
            );

            
            lightPositions[i * stride + 3] = this.lightSources[i].lightColor.x;
            lightPositions[i * stride + 4] = this.lightSources[i].lightColor.y;
            lightPositions[i * stride + 5] = this.lightSources[i].lightColor.z;

            lightPositions[i * stride + 6] = this.lightSources[i].lightStrength;
        }


        let lightPositionsUniformLoc = shader.getUniformLoc("lightPositions");
        let pointLightsUniformLoc = shader.getUniformLoc("pointLights");
        let lightColorUniformLoc = shader.getUniformLoc("lightColor");
        let lightSourceCountUniformLoc = shader.getUniformLoc("lightCount");

        //shader.setUniformVec3Array(lightPositionsUniformLoc, lightPositions);
        shader.setUniformFloatArray(lightPositionsUniformLoc, lightPositions);
        shader.setUniformFloatArray(pointLightsUniformLoc, lightPositions);
        shader.setUniformVec3f(lightColorUniformLoc, this.lightSources[0].lightColor);
        shader.setUniformInt(lightSourceCountUniformLoc, this.lightSources.length);
    }

    draw() {
        this.sceneObjects.forEach((object)=> {
            object.render(this.shaders.get("main"));
        });

        this.lightSources.forEach((lightSource) => {
            //let currentPos = lightSource.transform.position;
            //currentPos.x = this.lightSourceOffsetX + (100.0 * Math.sin(Time.time * 0.001));
            //lightSource.setPosition([currentPos.x, currentPos.y, currentPos.z]);
            lightSource.render(this.shaders.get("ambient"));
        });
    }
}

export default Scene;
