import Time from "./Time";

class Scene {
    constructor(shaders) {
        this.sceneObjects = [];
        this.lightSources = [];
        this.shaders = shaders;
    }

    addGameObject(gameObject) {
        this.sceneObjects.push(gameObject);
    }

    addLightSource(lightSource) {
        let lightCount = this.lightSources.length;
        let shader = this.shaders.get("main");
        shader.activate();

        let positionLocName = "pointLights[" + lightCount + "].position";
        shader.setUniformVec3f(shader.getUniformLoc(positionLocName), lightSource.position);

        let colorLocName = shader.getUniformLoc("pointLights[" + lightCount + "].color");
        shader.setUniformVec3f(colorLocName, lightSource.lightColor);

        let strengthLocName = "pointLights[" + lightCount + "].strength";
        shader.setUniformFloat(shader.getUniformLoc(strengthLocName), lightSource.lightStrength);

        let lightSourceCountUniformLoc = shader.getUniformLoc("lightCount");
        shader.setUniformInt(lightSourceCountUniformLoc, lightCount);

        this.lightSources.push(lightSource);
    }

    draw() {
        this.sceneObjects.forEach((object)=> {
            object.render(this.shaders.get("main"));
        });

        this.lightSources.forEach((lightSource) => {
            //let currentPos = lightSource.transform.position;
            //currentPos.x = this.lightSourceOffsetX + (100.0 * Math.sin(Time.time * 0.001));
            //lightSource.setPosition([currentPos.x, currentPos.y, currentPos.z]);
            lightSource.render(this.shaders.get("ambient"));
        });
    }
}

export default Scene;
