// JavaScript components
import Shader from "./ShaderHandle";
import Time from "./Time";
import Camera from "./Camera";
import Mesh from "./Mesh";
import GameObject from "./GameObject";
import EventHandler from "./EventHandler";
import Scene from "./Scene";
import PointLightSource from "./PointLightSource";

// Shader files
import PhongLightingShader from "./shaders/PhongLightingShader";
import AmbientShader from "./shaders/AmbientShader";

/***** Settings *****/
// Default camera position and rotation
const CAMERA_POSITION = [0.0, 5.0, 80.0];
const CAMERA_ROTATION = [-5.0, 0.0, 0.0];

// Enum struct for creating different shapes
const SHAPE = {
    CUBE: 0,
    SPHERE: 1,
    CYLINDER_SMOOTH: 2,
    CYLINDER_SHARP: 3,
    PLANE: 4
};

class Engine {
    constructor() {
        // Initialize timing variables
        this.lastTime = 0.0;
        this.frameCount = 0;
        this.fps = 0;
        this.currentTextOffset = 0;
        this.shaders = new Map();
        this.viewportAspectRatio = 1.0;

        // Scenes works like blocks in the game world.
        // The active scenes are based on the cameras distance to the origin of each scene.
        this.scenes = [];
    }

    /*
    * @brief This is the engines main update function and is called every iteration of the main loop
    */
    run() {
        // Update time clock
        Time.update();

        // Handle camera navigation
        this.eventHandler.processCameraNavigation(this.camera);

        // Render graphics
        this.render();
    }

    render() {
        // Set the viewport to the canvas dimensions and update the aspect ratio for the projection matrix
        this.updateViewportDimensions();

        // Clear screen 3D
        this.gl.clear(this.gl.DEPTH_BUFFER_BIT | this.gl.COLOR_BUFFER_BIT);

        // Draw scenes
        this.scenes.forEach((scene) => {
            scene.updateLightPositions();
            scene.draw();
        });

        // Display fps and other information
        this.drawTextElements();
    }

    updateViewportDimensions() {
        // Calculate current aspect ratio
        let currentAspectRatio = this.canvas.width / this.canvas.height;

        // Check if the aspect ratio has changed before going further
        if (Math.abs(this.viewportAspectRatio - currentAspectRatio) < 0.001)
            return;

        // Update the projection matrix for the shaders
        this.shaders.forEach((shader) => {
            shader.activate();
            shader.setProjectionUniformAspectRatio(currentAspectRatio);
        });

        // Update the viewport dimensions to the canvas width
        this.gl.viewport(0, 0, this.canvas.width, this.canvas.height);

        // Save current aspect ratio
        this.viewportAspectRatio = currentAspectRatio;
    }

    preload() {
        // Initiate the mouse event handler
        this.eventHandler = new EventHandler();

        // 2D Canvas for text display
        this.canvas2D = document.getElementById('text-canvas');
        this.ctx = this.canvas2D.getContext('2d');

        // WebGL canvas
        this.canvas = document.getElementById("gl-canvas");
        // Initialize the WebGL context
        this.gl = this.canvas.getContext("webgl");

        // If WebGL isn't supported
        if (!this.gl) {
            console.log("WebGL is not supported");
            alert("Unable to initialize WebGL. Your browser or machine may not support it.");
            return 0;
        }

        // Set the viewport dimensions to the same as the canvas
        this.gl.viewport(0, 0, this.canvas.width, this.canvas.height);
        // Set clear color to dark blue, fully opaque
        this.gl.clearColor(0.02, 0.02, 0.02, 1.0);
        // Enable depth so that triangles closer to the camera don't get overlapped by those further away
        this.gl.enable(this.gl.DEPTH_TEST);
        // Only draw "visible" sides (memory saving)
        this.gl.enable(this.gl.CULL_FACE);
        this.gl.cullFace(this.gl.BACK);
        this.gl.frontFace(this.gl.CCW);

        // Create shaders
        let mainShader = new Shader(this.canvas, PhongLightingShader.vertex, PhongLightingShader.fragment);
        let ambientShader = new Shader(this.canvas, AmbientShader.vertex, AmbientShader.fragment);

        this.shaders.set("phong", mainShader);
        this.shaders.set("ambient", ambientShader);

        // Initialize the main camera
        this.camera = new Camera(this.shaders);
        this.camera.setPosition(CAMERA_POSITION);
        this.camera.setRotation(CAMERA_ROTATION);

        // Create a scene for the objects
        let defaultScene = new Scene(this.shaders);
        let defaultScene2 = new Scene(this.shaders);
        let defaultScene3 = new Scene(this.shaders);
        let defaultScene4 = new Scene(this.shaders);

        // Create scene objects
        this.loadDefaultSceneObjects(defaultScene);
        this.loadDefaultSceneObjects(defaultScene2);
        this.loadDefaultSceneObjects(defaultScene3);
        this.loadDefaultSceneObjects(defaultScene4);

        defaultScene3.setPosition([0.0, 0.0, 80.0]);
        defaultScene3.setRotation([0.0, 180.0, 0.0]);

        defaultScene2.setPosition([0.0, 60.0, 0.0]);
        defaultScene2.setRotation([0.0, 0.0, 180.0]);

        defaultScene4.setPosition([0.0, 60.0, 80.0]);
        defaultScene4.setRotation([0.0, 180.0, 180.0]);

        // Add to scenes
        this.scenes.push(defaultScene);
        this.scenes.push(defaultScene2);
        this.scenes.push(defaultScene3);
        this.scenes.push(defaultScene4);
    }

    drawTextElements() {
        // Set text font style
        this.ctx.font = "20px Consolas";
        this.ctx.fillStyle = 'white';
        this.currentTextOffset = 0;

        // Clear previous
        this.ctx.clearRect(this.currentTextOffset, 0, this.canvas.offsetWidth, 30);

        // Draw the text
        this.displayFrameRate();
        this.displayCameraPosition();
    }

    displayFrameRate() {
        let currentTime = Time.time;
        // If tNow - tLast >= 1000, one second has passed
        if ((currentTime - this.lastTime) >= 1000.0) {
            // Fps = the number of frames
            this.fps = this.frameCount;
            // Update timer
            this.lastTime = currentTime;
            // Reset frame count
            this.frameCount = 0;
        }
        else {
            ++this.frameCount;
        }

        // Compose text string
        let fpsText = "Frame rate: " + this.fps + " fps";
        // Draw fps and frame time in milliseconds
        this.ctx.fillText(fpsText, 10, 25);
        // Update text offset for next text
        this.currentTextOffset = fpsText.length * 12 + 50;
    }

    displayCameraPosition() {
        let pos = this.camera.transform.position;
        // Compose text string
        let cameraPositionText = "Camera position: (" + pos.x.toFixed(2) + ", " + pos.y.toFixed(2) + ", " + pos.z.toFixed(2) + ")";

        // Draw fps and frame time in milliseconds
        this.ctx.fillText(cameraPositionText, this.currentTextOffset, 25);
        // Update text offset for next text
        this.currentTextOffset = cameraPositionText.length * 12;
    }

    createGameObject (settings) {
        let mesh = new Mesh(this.gl);
        if (settings.shape === SHAPE.CUBE) {
            mesh.createCube();
        }
        else if (settings.shape === SHAPE.SPHERE) {
            mesh.createSphere(32);
        }
        else if (settings.shape === SHAPE.CYLINDER_SMOOTH) {
            mesh.createCylinderSmooth(32, 1, 1, 2);
        }
        else if(settings.shape === SHAPE.CYLINDER_SHARP) {
            mesh.createCylinderSharp(8, 1, 2);
        }
        else if (settings.shape === SHAPE.PLANE) {
            mesh.createPlane(50,50);
        }
        mesh.setColor(settings.color);

        let obj = new GameObject (settings.name);
        obj.setPosition(settings.position);
        obj.setScale(settings.scale);
        obj.addMesh(mesh);

        return obj;
    }

    createPointLightSource(settings) {
        let lightSource = new PointLightSource(settings.name);
        lightSource.setPosition(settings.position);
        lightSource.setScale(settings.scale);
        lightSource.setLightColor(settings.color);
        lightSource.setAttenuation(settings.attenuation.kLinear, settings.attenuation.kQuadratic);

        // Create a model for the light source
        let lightSourceModel = new Mesh(this.gl);
        lightSourceModel.createSphere(8);
        lightSourceModel.setColor(settings.color);
        lightSource.addMesh(lightSourceModel);

        return lightSource;
    }

    loadDefaultSceneObjects(scene) {
        // Create a red cube
        let cube = this.createGameObject({
            shape: SHAPE.CUBE,
            name: 'Red Cube',
            scale: [1.0, 1.0, 1.0],
            position: [0.0, 1.5, 0.0],
            color: [1.0, 0.0, 0.0]
        });
        scene.addGameObject(cube);

        // Create a green sphere
        scene.addGameObject(
            this.createGameObject({
                shape: SHAPE.SPHERE,
                name: 'Green Sphere',
                scale: [1.0, 1.0, 1.0],
                position: [4.0, 2.0, 2.0],
                color: [0.0, 1.0, 0.0]
            })
        );

        // Create a smooth magenta cylinder
        scene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CYLINDER_SMOOTH,
                name: 'Magenta Cylinder',
                scale: [1.0, 1.0, 1.0],
                position: [-4.0, 2.0, 2.0],
                color: [1.0, 0.0, 1.0]
            })
        );

        // Create a sharp orange cylinder
        scene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CYLINDER_SHARP,
                name: 'Orange Cylinder',
                scale: [1.0, 1.0, 1.0],
                position: [-8.0, 2.0, 2.0],
                color: [1.0, 0.5, 0.0]
            })
        );

        // Create gray floor
        scene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Roof',
                scale: [40.0, 0.1, 40.0],
                position: [0.0, 0.0, 0.0],
                color: [0.75, 0.75, 0.75]
            })
        );

        // Create blue walls
        let wallColor = [0.0, 0.2, 1.0];
        let wallHeight = 10.0;
        scene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Left Wall',
                scale: [0.1, wallHeight, 20.0],
                position: [-40.0, wallHeight, -20.0],
                color: wallColor
            })
        );
        scene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Right Wall',
                scale: [0.1, wallHeight, 20.0],
                position: [40.0, wallHeight, -20.0],
                color: wallColor
            })
        );
        scene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Back Wall',
                scale: [40.0, wallHeight, 0.1],
                position: [0.0, wallHeight, -40.0],
                color: wallColor
            })
        );

        // Create gray roof
        scene.addGameObject(
            this.createGameObject({
                shape: SHAPE.CUBE,
                name: 'Floor',
                scale: [40.0, 0.1, 20.0],
                position: [0.0, wallHeight * 2.0, -20.0],
                color: [0.5, 0.5, 0.5]
            })
        );

        // Create a point lights for the scene
        // Add the light source to our scene
        let lampRadius = 0.5;
        scene.addLightSource(this.createPointLightSource({
            name: "Middle Lamp",
            scale: [lampRadius, lampRadius, lampRadius],
            position: [0.0, 2.0 * wallHeight - lampRadius, -35.0],
            color: [1.0, 1.0, 1.0],
            attenuation: { // The attenuation constants should make the light travel a maximum distance of 325
                kLinear: 0.014,
                kQuadratic: 0.0007
            }
        }));
        scene.addLightSource(this.createPointLightSource({
            name: "Right Lamp",
            scale: [lampRadius, lampRadius, lampRadius],
            position: [37.5, 2.0 * wallHeight - lampRadius, -15.0],
            color: [1.0, 1.0, 1.0],
            attenuation: { // The attenuation constants should make the light travel a maximum distance of 325
                kLinear: 0.014,
                kQuadratic: 0.0007
            }
        }));
        scene.addLightSource(this.createPointLightSource({
            name: "Left Lamp",
            scale: [lampRadius, lampRadius, lampRadius],
            position: [-37.5, 2.0 * wallHeight - lampRadius, -15.0],
            color: [1.0, 1.0, 1.0],
            attenuation: { // The attenuation constants should make the light travel a maximum distance of 325
                kLinear: 0.014,
                kQuadratic: 0.0007
            }
        }));
    }
