class MouseEvent {
    static init() {
        this.xPos = 0;
        this.yPos = 0;
        this.moveX = 0;
        this.moveY = 0;
        this.wheelDelta = 0;
        this.isPressed = {
            leftButton: false,
            rightButton: false,
            middleButton: false
        };

        this.down = false;
        this.up = false;
    }

    static setOffsetX(offset) {
        this.xPos = offset;
    }
    
    static get offsetX() {
        return this.xPos;
    }

    static setMouseDownEvent(state) {
        this.down = state;
    }

    static get mouseDown() {
        return this.down;
    }
}

export default MouseEvent;
