class MouseEvent {
    static init() {
        this.xPos = 0;
        this.yPos = 0;
        this.moveX = 0;
        this.moveY = 0;
        this.wheelDelta = 0;
        this.isPressed = {
            leftButton: false,
            rightButton: false,
            middleButton: false
        };

        this.down = false;
        this.up = false;
    }

    // Setters
    static setOffsetX(offset) {
        this.xPos = offset;
    }

    static setOffsetY(offset) {
        this.yPos = offset;
    }

    static setMouseDownEvent(state) {
        this.down = state;
    }

    static setMouseUpEvent(state) {
        this.up = state;
    }

    // Getters
    static get offsetX() {
        return this.xPos;
    }

    static get offsetY() {
        return this.yPos;
    }

    static get mouseDownEvent() {
        return this.down;
    }

    static get mouseUpEvent() {
        return this.up;
    }
}

export default MouseEvent;
