import Vector3 from "./math/Vector3";
import RigidBody from "./RigidBody";

class ParticleEmitter {
    constructor (particleModel, particleSpeed, particleReach) {
        this.activeParticles = [];
        this.model = particleModel;
        this.speed = particleSpeed;
        this.reach = particleReach;

        this.lifeTime = 10.0;

        this.gravityIsEnabled = false;
        this.gravity = 9.82;
    }

    emitParticle(origin, direction) {
        let rb = new RigidBody(origin);
        rb.setVelocity([direction.x * this.speed, direction.y * this.speed, direction.z * this.speed]);
        rb.enableGravity(this.gravityIsEnabled);

        this.activeParticles.push({
            body: rb,
            origin: new Vector3(origin),
            time: 0.0
        });
    }

    enableGravity(state) {
        this.gravityIsEnabled = state;

        this.activeParticles.forEach((bullet) => {
            bullet.body.enableGravity(state);
        });
    }

    update(timeStep) {
        let i = 0;
        while(i < this.activeParticles.length) {
            // Update rigid body motion
            this.activeParticles[i].body.update(timeStep);

            // Check distance from start position
            if (this.activeParticles[i].time < this.lifeTime) {
                ++i;
            }
            else {// Remove particle if distance is greater than given reach
                this.activeParticles.splice(i, 1);
            }
        }
    }

    render(shader) {
        this.activeParticles.forEach((bullet) => {
            // Set bullet position
            this.model.setPosition([bullet.body.position.x, bullet.body.position.y, bullet.body.position.z]);
            // Draw bullet
            this.model.render(shader);
        })
    }
}

export default ParticleEmitter;
