import Vector3 from "./math/Vector3";
import RigidBody from "./RigidBody";

class Bullets {
    constructor (bulletModel, bulletSpeed, bulletReach) {
        this.activeBullets = [];
        this.model = bulletModel;
        this.speed = bulletSpeed;
        this.reach = bulletReach;

        this.gravityIsEnabled = false;
        this.gravity = 9.82;
    }

    addBullet(origin, direction) {
        let rb = new RigidBody(origin);
        rb.setVelocity([direction.x * this.speed, direction.y * this.speed, direction.z * this.speed]);
        rb.enableGravity(this.gravityIsEnabled);

        this.activeBullets.push({
            body: rb,
            origin: new Vector3(origin),
        });
    }

    enableGravity(state) {
        this.gravityIsEnabled = state;

        this.activeBullets.forEach((bullet) => {
            bullet.body.enableGravity(state);
        });
    }

    update(timeStep) {
        let i = 0;
        while(i < this.activeBullets.length) {
            // Update rigid body motion
            this.activeBullets[i].body.update(timeStep);

            // Check distance from start position
            if (Vector3.distance(this.activeBullets[i].body.position, this.activeBullets[i].origin) < this.reach) {
                ++i;
            }
            else {// Remove bullet if distance is greater than reach
                this.activeBullets.splice(i, 1);
            }
        }
    }

    render(shader) {
        this.activeBullets.forEach((bullet) => {
            // Set bullet position
            this.model.setPosition([bullet.body.position.x, bullet.body.position.y, bullet.body.position.z]);
            // Draw bullet
            this.model.render(shader);
        })
    }
}

export default Bullets;
