import Matrix4 from './math/Matrix4';
import Vector3 from './math/Vector3';

class Transform {
    constructor() {
        this.position = new Vector3([0.0, 0.0, 0.0]);
        this.rotation = new Vector3([0.0, 0.0, 0.0]);
        this.scaling = new Vector3([1.0, 1.0, 1.0]);

        this.matrix = Matrix4.identity();
        this.parent = null;
        this.children = [];
    }

    clone() {
        let clone = new Transform();
        clone.setPosition([this.position.x, this.position.y, this.position.z]);
        clone.setScale([this.scaling.x, this.scaling.y, this.scaling.z]);
        clone.setRotation([this.rotation.x, this.rotation.y, this.rotation.z]);
        clone.setParent(this.parent);
        return clone;
    }

    setScale(scale) {
        this.scaling.set(scale);
        this.composeMatrix();

        for (let i = 0; i < this.children.length; ++i) {
            this.children[i].setParent(this);
        }
    }

    setPosition(position) {
        this.position.set(position);
        this.composeMatrix();

        for (let i = 0; i < this.children.length; ++i) {
            this.children[i].setParent(this);
        }
    }

    setRotation (rotation) {
        this.rotation.set(rotation);
        this.composeMatrix();

        for (let i = 0; i < this.children.length; ++i) {
            this.children[i].setParent(this);
        }
    }

    translate(val) {
        let rightVec = new Vector3([this.matrix[0]*val[0], this.matrix[4]*val[0], this.matrix[8]*val[0]]);
        let upVec = new Vector3([this.matrix[1]*val[1], this.matrix[5]*val[1], this.matrix[9]*val[1]]);
        let forwardVec = new Vector3([this.matrix[2]*val[2], this.matrix[6]*val[2], this.matrix[10]*val[2]]);

        this.setPosition([
            this.position.x + rightVec.x/this.scaling.x - upVec.x/this.scaling.x - forwardVec.x/this.scaling.x,
            this.position.y - rightVec.y/this.scaling.y + upVec.y/this.scaling.y - forwardVec.y/this.scaling.y,
            this.position.z - rightVec.z/this.scaling.z - upVec.z/this.scaling.z + forwardVec.z/this.scaling.z,
        ]);

        for (let i = 0; i < this.children.length; ++i) {
            this.children[i].setParent(this);
        }
    }

    setParent(parentTransform) {
        this.parent = parentTransform;
        this.composeMatrix();
    }

    addChild(child) {
        this.children.push(child);
        child.setParent(this);
    }

    invert () {
        return Matrix4.invert(this.matrix, this.matrix);
    }

    composeMatrix () {
        let scaleMatrix = Matrix4.scaling(this.scaling.x, this.scaling.y, this.scaling.z);
        let rotationMatrix = Matrix4.rotateZ(Matrix4.identity(), (this.rotation.z*Math.PI/180));
        rotationMatrix = Matrix4.rotateX(rotationMatrix, (this.rotation.x*Math.PI/180));
        rotationMatrix = Matrix4.rotateY(rotationMatrix, (this.rotation.y*Math.PI/180));
        let translationMatrix = Matrix4.translation(this.position.x, this.position.y, this.position.z);

        this.matrix = Matrix4.multiply(rotationMatrix, scaleMatrix);
        this.matrix = Matrix4.multiply(translationMatrix, this.matrix);

        if (this.parent !== null) {
            this.matrix = Matrix4.multiply(this.parent.matrix, this.matrix);
        }
    }
}

export default Transform;
