import MouseEvent from "./events/MouseEvent";

class GameEditor {
    constructor(gl, shaders, camera, canvas, projectionMatrix) {
        this.gl = gl;
        this.shaders = shaders;
        this.camera = camera;
        this.canvas = canvas;
        this.projectionMatrix = projectionMatrix;
    }

    processUserInput() {
        // Handle camera navigation
        this.processCameraNavigation();

        // If left mouse button is pressed, cast a ray from the mouse cursor position
        if(MouseEvent.mouseDown && MouseEvent.isPressed.leftButton) {
            // Create a ray from the camera
            let ray = Raycast.raycastFromMouseCursor(MouseEvent.offsetX, MouseEvent.offsetY, this.camera.transform, this.projectionMatrix, this.canvas.width, this.canvas.height);

            //// Test ray intersections ////
            let objectFound = Raycast.findObjectIntersection(ray);
            if (objectFound) {
                if (this.selectedObject)
                    this.selectedObject.setActive(false);

                objectFound.setActive(true);
                this.selectedObject = objectFound;
            }
        }
    }

    processCameraNavigation () {
        // Zoom camera view in and out
        if (MouseEvent.isPressed.middleButton) {
            this.camera.translate([0.0, 0.0, -(MouseEvent.movementY + MouseEvent.movementX)/20.0]);
        } // Pan the camera view
        else if(MouseEvent.isPressed.leftButton) {
            this.camera.translate([-MouseEvent.movementX/40.0, MouseEvent.movementY/40.0, 0.0]);
        } // Rotate the camera view
        else if (MouseEvent.isPressed.rightButton) {
            this.camera.rotate([-MouseEvent.movementY/10.0, -MouseEvent.movementX/10.0, 0.0]);
        }

        if (Math.abs(MouseEvent.wheelDelta) > 0.01) {
            if (MouseEvent.wheelDelta > 0.0) {
                this.camera.translate([0.0, 0.0, 0.5]);
            }
            else
                this.camera.translate([0.0, 0.0, -0.5]);
        }
    }

}

export default GameEditor;
