// JavaScript
import Transform from './Transform';
import Vector3 from './math/Vector3';

class Camera {
    constructor(shader) {
        this.shaderListInfo = [];
        this.shaderListInfo.push({
            shaderProgram: shader,
            cameraView: shader.getUniformLoc("cameraView"),
            cameraPos: shader.getUniformLoc("viewPosition")
        });
        /*
        shader.activate();
        this.cameraViewUniformLocation = shader.getUniformLoc("cameraView");
        this.cameraPositionUniformLocation = shader.getUniformLoc("viewPosition");
        */

        this.transform = new Transform();
        this.updateUniforms();
    }

    translate (val) {
        let rightVec = new Vector3([this.transform.matrix[0]*val[0], this.transform.matrix[4]*val[0], this.transform.matrix[8]*val[0]]);
        let upVec = new Vector3([this.transform.matrix[1]*val[1], this.transform.matrix[5]*val[1], this.transform.matrix[9]*val[1]]);
        let forwardVec = new Vector3([this.transform.matrix[2]*val[2], this.transform.matrix[6]*val[2], this.transform.matrix[10]*val[2]]);

        this.setPosition([
            this.transform.position.x + rightVec.x + upVec.x + forwardVec.x,
            this.transform.position.y + rightVec.y + upVec.y + forwardVec.y,
            this.transform.position.z + rightVec.z + upVec.z + forwardVec.z,
        ]);
    }

    rotate (rot) {
        this.setRotation([
            this.transform.rotation.x + rot[0],
            this.transform.rotation.y + rot[1],
            this.transform.rotation.z + rot[2]
        ]);
    }

    setPosition(cameraPosition) {
        this.transform.setPosition(cameraPosition);
        //this.updateCameraViewUniform();
        //this.updateCameraPositionUniform();
    }

    setRotation(cameraRotation) {
        this.transform.setRotation(cameraRotation);
        //this.updateCameraViewUniform();
    }

    rotateX (angle) {
        this.setRotation([angle, this.transform.rotation.y, this.transform.rotation.z]);
    }

    rotateY (angle) {
        this.setRotation([this.transform.rotation.x, angle, this.transform.rotation.z]);
    }

    /*
    updateCameraViewUniform () {
        this.shader.activate();
        this.shader.setUniformMat4f(this.cameraViewUniformLocation, this.transform.inverted());
    }

    updateCameraPositionUniform() {
        this.shader.activate();
        this.shader.setUniformVec3f(this.cameraPositionUniformLocation, this.transform.position);
    }
       */

    updateUniforms() {
        for (let i = 0; i < this.shaderListInfo.length; ++i) {
            let info = this.shaderListInfo[i];
            info.shaderProgram.activate();
            info.shaderProgram.setUniformMat4f(info.cameraView, this.transform.inverted());
        }
    }
}

export default Camera;
