import GameObject from "./GameObject";
import Vector3 from "./math/Vector3";

class PointLightSource extends GameObject{
    constructor(name) {
        super(name);
        // Initialize light color as white
        this.lightColor = new Vector3([1.0, 1.0, 1.0]);
        this.lightStrength = 100.0;
        this.attenuationSettings = {
            kLinear: 0.014,
            kQuadratic: 0.00014
        };
    }

    setPosition(pos) {
        super.setPosition(pos);
    }

    setLightColor(color) {
        this.lightColor.set(color);
    }

    setLightStrength(strength) {
        this.lightStrength = strength;
    }
}

export default PointLightSource;
