// JavaScript
import Shader from "./Shader";
import Time from "./Time";
import Camera from "./Camera";
import Mesh from "./Mesh";
import GameObject from "./GameObject";
import EventHandler from "./EventHandler";
import GraphicsEngine from "./GraphicsEngine";

/***** Settings *****/
// Default camera position and rotation
const CAMERA_POSITION = [0.0, 100.0, 500.0];
const CAMERA_ROTATION = [-15.0, 0.0, 0.0];

// Enum struct for creating different shapes
const SHAPE = {
    CUBE: 0,
    SPHERE: 1,
    CYLINDER: 2,
    PLANE: 3
};

class Engine {
    constructor() {
        this.sceneObjects = [];
        this.graphicsEngine = new GraphicsEngine(this.sceneObjects);
    }

    /*
    * @brief This is the engines main update function and is called every iteration of the main loop
    */
    run() {
        // Keeps track of time passed and delta time
        Time.update();

        // Draw all graphics
        this.graphicsEngine.run();
    }

    preload() {
        // Set up webgl context
        this.graphicsEngine.preload();
    }
}

export default Engine;
