import Vector3 from "./math/Vector3";

class Raycast {
    static sphereIntersectionTest(ray, sphere) {
        let rayToSphere = Vector3.subtract(sphere.center, ray.origin);
        let tca = Vector3.dot(rayToSphere, ray.direction);
        if (tca < 0.0) {
            console.log("Sphere intersection missed");
            return false;
        }

        let shortestFromRayToCenter = Math.sqrt(Vector3.dot(rayToSphere, rayToSphere) - tca*tca);
        // If this distance is greater than the sphere radius the ray has missed the sphere
        if (shortestFromRayToCenter > sphere.radius) {
            console.log("Sphere intersection missed");
            return false;
        }

        //let thc = Math.sqrt(sphere.radius*sphere.radius - shortestFromRayToCenter*shortestFromRayToCenter);
        //let intersectionDistancePoint1 = tca - thc;
        //let intersectionDistancePoint2 = tca + thc;

        console.log("Sphere intersection hit");
        return true;
    }

    static planeIntersectionTest(ray, plane) {
        // Compute distance from ray origin and intersection point
        let intersectionPointDist = -(Vector3.dot(ray.origin, plane.normal) + plane.offset) / Vector3.dot(ray.direction, plane.normal);

        if (intersectionPointDist < 0) {
            console.log("Plane intersection missed");
            return false;
        }

        console.log("Intersection point distance: " + intersectionPointDist);

        let intersectionPoint = new Vector3([
            ray.origin.x + ray.direction.x*intersectionPointDist,
            ray.origin.y + ray.direction.y*intersectionPointDist,
            ray.origin.z + ray.direction.z*intersectionPointDist,
        ]);

        console.log("Intersection point: " + intersectionPoint.x.toFixed(2) + ", " + intersectionPoint.y.toFixed(2) + ", " + intersectionPoint.z.toFixed(2));
        return true;
    }
}

export default Raycast;
