import GameObject from "./GameObject";
import Vector3 from "./math/Vector3";

class PointLightSource extends GameObject{
    constructor(name, shader) {
        super(name, shader);

        // Initialize light color as white
        this.lightColor = new Vector3([1.0, 1.0, 1.0]);

        // Get shader uniform locations for light position and light color
        this.shader.use();
        this.lightPositionUniformLocation = this.shader.getUniformLoc("lightPosition");
        this.lightColorUniformLocation = this.shader.getUniformLoc("lightColor");

        this.updatePositionUniform();
        this.updateColorUniform();
    }

    setPosition(pos) {
        super.setPosition(pos);
        this.updatePositionUniform();
    }

    setColor(color) {
        this.lightColor.set(color);
        this.updateColorUniform();
    }

    updatePositionUniform() {
        this.shader.setUniformVec3f(this.lightPositionUniformLocation, this.transform.position);
    }

    updateColorUniform() {
        this.shader.setUniformVec3f(this.lightColorUniformLocation, this.lightColor);
    }
}

export default PointLightSource;
