class MouseEventHandler {
    constructor(sceneObjects) {
        this.mouseInputReader = {
            leftClickIsActive: false,
            moveX: 0,
            moveY: 0,
            altKey: false,
            ctrlKey: false,
            shiftKey: false,
            selectedGameObjectIndex: 0,
            selectedGameObjectAxis: null,
            moveGameObjectX: 0,
            moveGameObjectY: 0
        };
        this.sceneObjects = sceneObjects;

        let mouseInput = this.mouseInputReader;
        let objects = this.sceneObjects;

        let canvas2D = document.getElementById('text-canvas');
        canvas2D.addEventListener("mousedown", function(event) {
            this.mouseDown()
        }, false);

        canvas2D.addEventListener("mouseup", function(event) {
            this.mouseUp(event);
        }, false);
        canvas2D.addEventListener("mouseleave", function(event) {
            this.mouseUp(event);
        }, false);
        canvas2D.addEventListener("mousemove", function(event) {
            this.mouseMove(event);
        }, false);
    }

    mouseDown(event) {
        switch(event.which) {
            case MouseKey.LEFT_CLICK:
                mouseInput.leftClickIsActive = true;
                mouseInput.altKey = event.altKey;
                mouseInput.ctrlKey = event.ctrlKey;
                mouseInput.shiftKey = event.shiftKey;

                // Extend the click box borders
                let offset = 4;

                for (let i = 0; i < objects.length; ++i) {
                    // Check if the coordinate system is visible
                    if (objects[i].localCoordinateSystemUI.isVisible) {
                        // Check if mouse is in the click box area
                        if (event.offsetX <= objects[i].localCoordinateSystemUI.clickBoxAxisX.borders.xMax+offset &&
                            event.offsetX >= objects[i].localCoordinateSystemUI.clickBoxAxisX.borders.xMin-offset &&
                            event.offsetY <= objects[i].localCoordinateSystemUI.clickBoxAxisX.borders.yMax+offset &&
                            event.offsetY >= objects[i].localCoordinateSystemUI.clickBoxAxisX.borders.yMin-offset) {

                            mouseInput.selectedGameObjectAxis = 'x';
                        }
                        else if (event.offsetX <= objects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMax+offset &&
                            event.offsetX >= objects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMin-offset &&
                            event.offsetY <= objects[i].localCoordinateSystemUI.clickBoxAxisY.borders.yMax+offset &&
                            event.offsetY >= objects[i].localCoordinateSystemUI.clickBoxAxisY.borders.yMin-offset) {

                            mouseInput.selectedGameObjectAxis = 'y';
                        }
                        else if (event.offsetX <= objects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMax+offset &&
                            event.offsetX >= objects[i].localCoordinateSystemUI.clickBoxAxisZ.borders.xMin-offset &&
                            event.offsetY <= objects[i].localCoordinateSystemUI.clickBoxAxisZ.borders.yMax+offset &&
                            event.offsetY >= objects[i].localCoordinateSystemUI.clickBoxAxisZ.borders.yMin-offset) {

                            mouseInput.selectedGameObjectAxis = 'z';
                        }

                        mouseInput.selectedGameObjectIndex = i;
                    }
                }
                break;
            case MouseKey.RIGHT_CLICK:
                break;
            default:
                break;
        }
    }

    mouseUp(event) {
        let mouseInput = this.mouseInputReader;

        switch(event.which) {
            case MouseKey.LEFT_CLICK:
                mouseInput.leftClickIsActive = false;
                mouseInput.selectedGameObjectAxis = null;
                break;
            case MouseKey.RIGHT_CLICK:
                break;
            default:
                break;
        }
    }

    mouseLeave(event) {
        let mouseInput = this.mouseInputReader;

        if (mouseInput.leftClickIsActive) {
            mouseInput.leftClickIsActive = false;
        }
    }

    mouseMove(event) {
        let mouseInput = this.mouseInputReader;

        if (mouseInput.leftClickIsActive) {
            mouseInput.moveX = event.movementX;
            mouseInput.moveY = event.movementY;
            mouseInput.moveGameObjectX = event.movementX;
            mouseInput.moveGameObjectY = event.movementY;
        }
    }

    processCameraMovement (camera) {
        if (this.mouseInputReader.leftClickIsActive) {
            // Move left/right and up/down
            if (this.mouseInputReader.shiftKey) {
                camera.translate([-this.mouseInputReader.moveX/4, this.mouseInputReader.moveY/4, 0.0]);
            } // Move forward/backwards
            else if (this.mouseInputReader.ctrlKey) {
                camera.translate([0.0, 0.0, -(this.mouseInputReader.moveY+this.mouseInputReader.moveX)/4]);
            }
            else if (this.mouseInputReader.altKey) {
                camera.rotate([-this.mouseInputReader.moveY/8, -this.mouseInputReader.moveX/8, 0.0]);
            }
            this.mouseInputReader.moveX = 0;
            this.mouseInputReader.moveY = 0;

            //this.UpdateGameObjectScreenPosition();
        }
    }
}

export default MouseEventHandler;
