import Vector3 from "./math/Vector3";
import Matrix4 from "./math/Matrix4";

class Raycast {
    static init() {
        this.intersectableObjects = [];
    }

    static raycastFromMouseCursor(mouseX, mouseY, cameraTransform, projectionMatrix, canvasWidth, canvasHeight) {
        // Transform from mouse cursor's viewport coordinates from [0 : width/height] to the range [-1.0 : 1.0]
        let xNorm = (mouseX / (canvasWidth  * 0.5)) - 1.0;
        let yNorm = 1.0 - (mouseY / (canvasHeight * 0.5)); // Reverse the Y-axis

        // Combine the transform matrix for perspective and camera view
        let transform = Matrix4.multiply(projectionMatrix, cameraTransform.matrix);
        // Invert the transform matrix since we are reversing the process (from normalized device coordinates to world coordinates)
        Matrix4.invert(transform, transform);

        // Transform screen coordinates to world coordinates
        let ndc = [xNorm, yNorm, 1.0, 1.0];
        let worldCoordinates = Matrix4.multiplyVector(transform, ndc);

        // Normalize the world coordinates to finish the ray direction vector
        const rayDirection = (new Vector3([worldCoordinates[0], worldCoordinates[1], worldCoordinates[2]])).normalized();
        const rayOrigin = cameraTransform.position.clone();

        return {
            direction: rayDirection,
            origin: rayOrigin
        };
    }

    static findObjectIntersection(ray) {
        let objectFound = null;

        let closestHit = 0;
        this.intersectableObjects.forEach((object) => {
            let t = this.sphereIntersectionTest(ray, object.collider);
            if (t > closestHit) {
                objectFound = object;
                closestHit = t;
            }
        });

        return objectFound;
    }

    static sphereIntersectionTest(ray, sphere) {
        let rayToSphere = Vector3.subtract(sphere.worldPosition, ray.origin);
        let tca = Vector3.dot(rayToSphere, ray.direction);

        // If tca is negative, the ray is facing the opposite direction of the sphere collider
        if (tca < 0.0) {
            return -1;
        }

        let shortestFromRayToCenter = Math.sqrt(Vector3.dot(rayToSphere, rayToSphere) - tca*tca);

        // If this distance is greater than the sphere radius the ray has missed the sphere
        if (shortestFromRayToCenter > sphere.radius) {
            return -1;
        }

        let thc = Math.sqrt(sphere.radius*sphere.radius - shortestFromRayToCenter*shortestFromRayToCenter);
        let intersectionDistancePoint1 = tca - thc;
        let intersectionDistancePoint2 = tca + thc;

        // Return the closest intersection of the two possible intersections
        return (intersectionDistancePoint1 < intersectionDistancePoint2) ? intersectionDistancePoint1 : intersectionDistancePoint2;
    }

    static planeIntersectionTest(ray, plane) {
        // Compute distance from ray origin and intersection point
        let intersectionPointDist = -(Vector3.dot(ray.origin, plane.normal) + plane.offset) / Vector3.dot(ray.direction, plane.normal);

        if (intersectionPointDist < 0) {
            console.log("Plane intersection missed");
            return false;
        }

        //console.log("Intersection point distance: " + intersectionPointDist);

        let intersectionPoint = new Vector3([
            ray.origin.x + ray.direction.x*intersectionPointDist,
            ray.origin.y + ray.direction.y*intersectionPointDist,
            ray.origin.z + ray.direction.z*intersectionPointDist,
        ]);

        //console.log("Intersection point: " + intersectionPoint.x.toFixed(2) + ", " + intersectionPoint.y.toFixed(2) + ", " + intersectionPoint.z.toFixed(2));
        return true;
    }

    static addIntersectableObject(gameObject) {
        this.intersectableObjects.push(gameObject);
    }
}

export default Raycast;
