class GameObject {
    constructor (name) {
        this.nameTag = name;
        this.meshes = [];
        this.transform = new Transform();
        this.collider = null;
        this.rigidBody = null;
        this.isActive = false;
    }

    get worldPosition() {
        return new Vector3([this.transform.matrix[12], this.transform.matrix[13], this.transform.matrix[14]]);
    }

    setNameTag(name) {
        this.nameTag = name;
    }

    translate (val) {
        this.transform.translate(val);
    }

    rotate(rot) {
        this.transform.rotate(rot);
    }

    setPosition(pos) {
        this.transform.setPosition(pos);
    }

    setRotation(rot) {
        this.transform.setRotation(rot);
    }

    setScale(scale) {
        this.transform.setScale(scale);
    }

    setActive(state) {
        this.isActive = state;

        if (this.collider !== null) {
            this.collider.setVisible(state);
        }
    }

    addMesh (mesh) {
        this.transform.addChild(mesh.transform);
        this.meshes.push(mesh);
    }

    addCollider (collider) {
        this.transform.addChild(collider.transform);
        this.collider = collider;
    }

    addRigidBody (rigidBody) {
        this.rigidBody = rigidBody;
    }

    render(shader) {
        for (let i = 0; i < this.meshes.length; ++i) {
            this.meshes[i].render(shader);
        }
    }

    update(timeStep) {
        if (this.rigidBody) {
            this.rigidBody.update(timeStep);
            this.transform.setPosition([this.rigidBody.position.x, this.rigidBody.position.y, this.rigidBody.position.z]);

            if (this.collider) {
                let plane = {
                    normal: [0.0, 1.0, 0.0],
                    offset: 0.0
                };
                let ray = this.rigidBody.velocity.clone();
                console.log(ray);
                ray.normalize();

                let intersectionPoint = Raycast.planeIntersectionTest(ray, plane);
                if (intersectionPoint !== null) {
                    if (Vector3.distance(intersectionPoint, this.collider.worldPosition) < this.collider.radius) {
                        this.rigidBody.setVelocity([0.0, 0.0, 0.0]);
                    }
                }
            }
        }

        this.customUpdate(timeStep);
    }

    customUpdate(timeStep) {

    }
}

export default GameObject;
