import SphereCollider from "./SphereCollider";
import Vector3 from "./math/Vector3";
import Raycast from "./Raycast";
import CollisionManager from "./CollisionManager";

class RigidBody {
    constructor(position, mass = 1.0) {
        this.position = new Vector3(position);
        this.velocity = new Vector3([0.0, 0.0, 0.0]);
        this.acceleration = new Vector3([0.0, 0.0, 0.0]);

        this.collider = new SphereCollider();

        this.mass = mass;
        this.enableGravity(true);
    }

    setVelocity(vel) {
        this.velocity.set(vel);
    }

    setAcceleration(acc) {
        this.acceleration.set(acc);
    }

    setPosition(pos) {
        this.position.set(pos);
    }

    enableGravity(state) {
        if (state)
            this.gravity = 9.82;
        else
            this.gravity = 0.0;
    }

    get gravityIsEnabled () {
        return this.gravity > 0.1;
    }

    update(s) {
        //if (this.velocity.magnitude < 0.1)
            //return false;
        let timeStep = 0.0001

        let nextVelocity = [
            this.velocity.x + this.acceleration.x * timeStep,
            this.velocity.y + (this.acceleration.y - this.gravity) * timeStep,
            this.velocity.z + this.acceleration.z * timeStep,
        ];
        this.velocity.set(nextVelocity);

        let nextPosition = [
            this.position.x + this.velocity.x * timeStep,
            this.position.y + this.velocity.y * timeStep,
            this.position.z + this.velocity.z * timeStep,
        ];
        this.position.set(nextPosition);

        this.handleCollision(timeStep);

        return true;
    }

    handleCollision(timeStep) {

        let planes = CollisionManager.getPlaneColliders();

        let rayDir = this.velocity.normalized();
        let rayOrigin = this.position.clone();

        planes.forEach((plane) => {
            let intersectionPoint = Raycast.planeIntersectionTest(rayDir, rayOrigin, plane);

            if (intersectionPoint !== null) {
                if (Vector3.distance(intersectionPoint, rayOrigin) < this.collider.radius*4.0) {
                    let reflectDirection = Vector3.subtract(plane.normal.clone().multipleBy(2.0 * Vector3.dot(plane.normal, rayDir)), rayDir);
                    let bounce = -this.velocity.magnitude*0.75;

                    this.velocity.set([
                        reflectDirection.x*bounce,
                        reflectDirection.y*bounce,
                        reflectDirection.z*bounce
                    ]);

                    let nextPosition = [
                        this.position.x + this.velocity.x * timeStep,
                        this.position.y + this.velocity.y * timeStep,
                        this.position.z + this.velocity.z * timeStep,
                    ];
                    this.position.set(nextPosition);
                }
            }
        });

    }
}

export default RigidBody;
