import Vector3 from "./math/Vector3";

class RigidBody {
    constructor(position, mass) {
        this.position = new Vector3(position);
        this.velocity = new Vector3([0.0, 0.0, 0.0]);
        this.acceleration = new Vector3([0.0, 0.0, 0.0]);
        this.mass = mass;
        this.step = 0.01;
    }

    update() {
        this.acceleration.set([0.0, -9.82, 0.0]);
        this.velocity = Vector3.add(this.velocity, this.acceleration*this.step);
        this.position = Vector3.add(this.position, this.velocity*this.step);

        console.log(this.position);
    }
}

export default RigidBody;
