        this.editor = new GameEditor(this.gl, this.context2D, this.shaders, this.camera, this.canvas, this.projectionMatrix);

        // Create a scene for the objects
        let defaultScene = new Scene(this.shaders);

        // Create scene objects
        this.loadDefaultSceneObjects(defaultScene);
        // Add to scenes
        this.scenes.push(defaultScene);

        // Create a bullet for displaying raycast
        let bulletModel = this.createGameObject({
            shape: SHAPE.SPHERE,
            name: 'Cyan Sphere',
            scale: [0.5, 0.5, 0.5],
            position: [0.0, 0.0, 0.0],
            color: [0.0, 1.0, 1.0]
        });

        this.raycastBullets = new Bullets(bulletModel, 50.0, 200.0);
        this.raycastBullets.enableGravity();
