import Transform from "./Transform";
import Mesh from "./Mesh";

class SphereCollider {
    constructor(gl, shader) {
        this.transform = new Transform();
        this.transform.setScale([1.5, 1.5, 1.5]);

        this.model = new Mesh(gl);
        this.model.createSphere(4);
        this.model.setColor([0.75, 0.75, 0.75]);
        this.model.setDrawMode(gl.LINE_STRIP);

        this.transform.addChild(this.model.transform);

        this.isVisible = false;
        this.radius = 1.0;

        this.shader = shader;
    }

    setVisible(state) {
        this.isVisible = state;
    }

    render() {
        if(this.isVisible) {
            this.model.render(this.shader);
        }
    }
}

export default SphereCollider;
