class Time {
    constructor() {
        this._time = window.performance.now();
        this._deltaTime = 0.0;
    }

    static get deltaTime() {
        return this._deltaTime;
    }

    static get time() {
        return this._time;
    }

    static update() {
        //console.log(this._deltaTime);
        this._deltaTime = window.performance.now() - this._time;
        this._time = window.performance.now();
    }
}

export default Time;
