import SphereCollider from "./SphereCollider";
import Vector3 from "./math/Vector3";
import Raycast from "./Raycast";
import CollisionManager from "./CollisionManager";

class RigidBody {
    constructor(position, mass = 1.0) {
        this.id = -1;
        this.position = new Vector3(position);
        this.velocity = new Vector3([0.0, 0.0, 0.0]);
        this.acceleration = new Vector3([0.0, 0.0, 0.0]);

        this.collider = new SphereCollider();

        this.mass = mass;
        this.enableGravity(true);

        this.inCollision = false;
    }

    setVelocity(vel) {
        this.velocity.set(vel);
    }

    setAcceleration(acc) {
        this.acceleration.set(acc);
    }

    setPosition(pos) {
        this.position.set(pos);
    }

    enableGravity(state) {
        if (state)
            this.gravity = 9.82;
        else
            this.gravity = 0.0;
    }

    get gravityIsEnabled () {
        return this.gravity > 0.1;
    }

    update(timeStep) {

        this.updateMotion(timeStep);
        this.handlePlaneCollisions(timeStep);

        this.scanForBodyCollisions(timeStep);

        return true;
    }

    updateMotion(timeStep) {
        this.velocity.set([
            this.velocity.x + this.acceleration.x * timeStep,
            this.velocity.y + (this.acceleration.y - this.gravity) * timeStep,
            this.velocity.z + this.acceleration.z * timeStep,
        ]);
        this.position.set([
            this.position.x + this.velocity.x * timeStep,
            this.position.y + this.velocity.y * timeStep,
            this.position.z + this.velocity.z * timeStep,
        ]);
    }

    handlePlaneCollisions(timeStep) {
        let planes = CollisionManager.getPlaneColliders();

        let rayDir = this.velocity.normalized();
        let rayOrigin = this.position.clone();

        // Find closest intersectionPoint
        planes.forEach((plane) => {
            // If the plane intersection test returns null, no plane was found
            let intersectionPoint = Raycast.planeIntersectionTest(rayDir, rayOrigin, plane);
            if (intersectionPoint !== null) {
                // Calculate the closest distance from the rigid body collider and the plane
                let distanceToPlane = Vector3.dot(plane.normal, Vector3.subtract(rayOrigin, intersectionPoint));

                // Check if the sphere is intersecting the plane
                if (distanceToPlane < this.collider.radius) {
                    // Move the object to the plane surface
                    let diff = (this.collider.radius - distanceToPlane);
                    this.position.set([
                        this.position.x + plane.normal.x * diff,
                        this.position.y + plane.normal.y * diff,
                        this.position.z + plane.normal.z * diff
                    ]);

                    // Calculate the bounce velocity
                    let bounce = this.velocity.magnitude*0.8;
                    let reflectDirection = Vector3.subtract(plane.normal.clone().multipleBy(2.0 * Vector3.dot(plane.normal, rayDir)), rayDir);

                    this.velocity.set([
                        -reflectDirection.x*bounce,
                        -reflectDirection.y*bounce,
                        -reflectDirection.z*bounce
                    ]);
                    this.position.set([
                        this.position.x + this.velocity.x * timeStep,
                        this.position.y + this.velocity.y * timeStep,
                        this.position.z + this.velocity.z * timeStep,
                    ]);
                }
            }
        });
    }

    scanForBodyCollisions(timeStep) {
        // If the rigid body is not moving, don't check for target collisions
        if (this.velocity.magnitude < 0.1) {
            return;
        }

        //TODO: Optimize collision scanning (if two bodies have collided, we dont need to check collision again in this update

        let rigidBodies = CollisionManager.getRigidBodies();
        rigidBodies.forEach((target) => {
            // Dont check for collision with itself
            if (this.id !== target.id){
                // Compute the distance between the two sphere colliders
                let targetDistance = Vector3.distance(target.position, this.position);
                // If the distance is within this collider radius and target collider radius, we have a collision
                if (targetDistance < (target.collider.radius + this.collider.radius)) {
                    this.handleBodyCollisions(timeStep, target, targetDistance);
                }
            }
        });
    }

    handleBodyCollisions(timeStep, target, targetDistance) {
        // Get velocity directions
        let thisVelocityDirection = this.velocity.normalized();
        let targetVelocityDirection = target.velocity.normalized();

        // Move the body outside of collision area
        let diff = (this.collider.radius + target.collider.radius) - targetDistance;
        this.position.set([
            this.position.x - thisVelocityDirection.x * diff,
            this.position.y - thisVelocityDirection.y * diff,
            this.position.z - thisVelocityDirection.z * diff
        ]);

        // The new velocity direction is given by the vector between the two bodies
        let thisDirectionAfter = Vector3.subtract(target.position, this.position).normalized();
        let targetDirectionAfter = thisDirectionAfter.inverted();

        // Compute a multiplier based on the angle between the before and after velocities.
        // Wider angles results in lesser impact (lower multipliers)
        let thisMultiplier = Math.abs(Vector3.dot(thisVelocityDirection, thisDirectionAfter));
        let targetMultiplier = Math.abs(Vector3.dot(targetVelocityDirection, targetDirectionAfter));

        // Compute the speeds before collision
        let thisSpeedBefore = this.velocity.magnitude;
        let targetSpeedBefore = target.velocity.magnitude;

        // Elastic Collision Coefficients
        let denominator = this.mass + target.mass;
        let A = (2.0 * this.mass * (1.0 - targetMultiplier)) / denominator;
        let B = ((thisMultiplier * target.mass) - this.mass) / denominator;

        // Set new velocity and position for target
        target.velocity.set([
            thisDirectionAfter.x * thisSpeedBefore * A + targetDirectionAfter.x * targetSpeedBefore * B,
            thisDirectionAfter.y * thisSpeedBefore * A + targetDirectionAfter.y * targetSpeedBefore * B,
            thisDirectionAfter.z * thisSpeedBefore * A + targetDirectionAfter.z * targetSpeedBefore * B
        ]);
        target.position.set([
            target.position.x + target.velocity.x * timeStep,
            target.position.y + target.velocity.y * timeStep,
            target.position.z + target.velocity.z * timeStep,
        ]);

        // Elastic Collision Coefficients
        A = ((thisMultiplier * this.mass) - target.mass) / denominator;
        B = (2.0 * target.mass * (1.0 - targetMultiplier)) / denominator;

        // Set new velocity and position
        this.velocity.set([
            thisDirectionAfter.x * thisSpeedBefore * A + targetDirectionAfter.x * targetSpeedBefore * B,
            thisDirectionAfter.y * thisSpeedBefore * A + targetDirectionAfter.y * targetSpeedBefore * B,
            thisDirectionAfter.z * thisSpeedBefore * A + targetDirectionAfter.z * targetSpeedBefore * B
        ]);
        this.position.set([
            this.position.x + this.velocity.x * timeStep,
            this.position.y + this.velocity.y * timeStep,
            this.position.z + this.velocity.z * timeStep,
        ]);
    }
}

export default RigidBody;
