class CollisionManager {
    static init() {
        this.colliders = [];
        this.rigidBodies = new Map();
        this.keyCount = 0;
    }

    static registerPlaneCollider(collider) {
        this.colliders.push(collider);
    }

    static getPlaneColliders() {
        return this.colliders;
    }

    static registerRigidBody(rigidBody) {
        rigidBody.id = this.keyCount;
        this.rigidBodies.set(this.keyCount, rigidBody);
        return this.keyCount++;
    }

    static getRigidBodies() {
        return this.rigidBodies;
    }

    static removeRigidBody(id) {
        this.rigidBodies.delete(id);
    }
}

export default CollisionManager;
