class Scene {
    constructor(camera, mainShader, ambientShader) {
        this.camera = camera;
        this.mainShader = mainShader;
        this.ambientShader = ambientShader;

        this.sceneObjects = [];
        this.lightSources = [];
    }

    addGameObject(gameObject) {
        this.sceneObjects.push(gameObject);
    }

    addLightSource(lightSource) {
        this.lightSources.push(lightSource);
    }

    draw() {
        this.mainShader.activate();
        this.mainShader.setCameraViewUniform(this.camera.transform.inverted());
        this.mainShader.setCameraPosUniform(this.camera.transform.position);

        this.sceneObjects.forEach((object)=> {
            object.render();
        });

        this.lightSources.forEach((lightSource) => {
            lightSource.render();
        });
    }
}

export default Scene;
