    drawRay() {
        if (this.rayVertices.length === 0)
            return;

        // Use the combined shader program object
        this.gl.useProgram(this.shaderProgram);

        /*======= Associating shaders to buffer objects ======*/

        // Bind vertex buffer object
        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.vertex_buffer);

        // Get the attribute location
        let coord = this.gl.getAttribLocation(this.shaderProgram, "coordinates");

        // Point an attribute to the currently bound VBO
        this.gl.vertexAttribPointer(coord, 3, this.gl.FLOAT, false, 0, 0);

        // Enable the attribute
        this.gl.enableVertexAttribArray(coord);

        let cameraView = this.gl.getUniformLocation(this.shaderProgram, "cameraView");
        let projection = this.gl.getUniformLocation(this.shaderProgram, "perspective");
        let projectionMatrix = Matrix4.perspective(Math.PI / 6, this.viewportAspectRatio, 0.1, 1000.0);
        // Link the uniform with the matrix variable
        this.gl.uniformMatrix4fv(projection, this.gl.FALSE, projectionMatrix);
        this.gl.uniformMatrix4fv(cameraView, this.gl.FALSE, this.camera.transform.matrix);

        /*============ Drawing the triangle =============*/

        // Draw the triangle
        this.gl.drawArrays(this.gl.LINE_STRIP, 0, 10);

        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, null);
        // POINTS, LINE_STRIP, LINE_LOOP, LINES,
        // TRIANGLE_STRIP,TRIANGLE_FAN, TRIANGLES
    }

    createRay() {

        // Create an empty buffer object
        this.vertex_buffer = this.gl.createBuffer();

        // Bind appropriate array buffer to it
        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.vertex_buffer);

        // Pass the vertex data to the buffer
        this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(this.rayVertices), this.gl.STATIC_DRAW);

        // Unbind the buffer
        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, null);

        /*=================== Shaders ====================*/

        // Vertex shader source code
        let vertCode =
            'attribute vec3 coordinates;' +
            'uniform mat4 cameraView;' +
            'uniform mat4 projection;' +
            'void main(void) {' +
                'gl_Position = projection * cameraView * vec4(coordinates, 1.0);' +
            '}';

        // Create a vertex shader object
        let vertShader = this.gl.createShader(this.gl.VERTEX_SHADER);

        // Attach vertex shader source code
        this.gl.shaderSource(vertShader, vertCode);

        // Compile the vertex shader
        this.gl.compileShader(vertShader);

        // Fragment shader source code
        let fragCode =
            'void main(void) {' +
                'gl_FragColor = vec4(1.0, 0.0, 0.0, 1.0);' +
            '}';

        // Create fragment shader object
        let fragShader = this.gl.createShader(this.gl.FRAGMENT_SHADER);

        // Attach fragment shader source code
        this.gl.shaderSource(fragShader, fragCode);

        // Compile the fragmentt shader
        this.gl.compileShader(fragShader);

        // Create a shader program object to store
        // the combined shader program
        this.shaderProgram = this.gl.createProgram();

        // Attach a vertex shader
        this.gl.attachShader(this.shaderProgram, vertShader);

        // Attach a fragment shader
        this.gl.attachShader(this.shaderProgram, fragShader);

        // Link both the programs
        this.gl.linkProgram(this.shaderProgram);
    }
