// Enum struct for handling mouse events
const MouseKey = {
    LEFT: 1,
    MIDDLE: 2,
    RIGHT: 3
};

class EventHandler {
    constructor(sceneObjects) {
        this.sceneObjects = sceneObjects;
        this.mouseEvent = {
            offsetX: 0,
            offsetY: 0,
            movementX: 0,
            movementY: 0,
            deltaX: 0,
            deltaY: 0,
            wheelDelta: 0,
            isPressed: {
                leftButton: false,
                rightButton: false,
                middleButton: false
            }
        };
        this.objectInteraction = {
            selectedGameObjectIndex: 0,
            selectedGameObjectAxis: null,
            moveGameObjectX: 0,
            moveGameObjectY: 0
        };

        let me = this.mouseEvent;
        let oi = this.objectInteraction;

        /*
        this.mouseInputReader = {
            leftClickIsActive: false,
            rightClickIsActive: false,
            moveX: 0,
            moveY: 0,
            altKey: false,
            ctrlKey: false,
            shiftKey: false,
            selectedGameObjectIndex: 0,
            selectedGameObjectAxis: null,
            moveGameObjectX: 0,
            moveGameObjectY: 0,
            wheelDelta: 0
        };
        let mouseInput = this.mouseInputReader;
        */

        let canvas2D = document.getElementById('text-canvas');

        // Mouse down event function definition
        canvas2D.onmousedown = function(event) {
            if(event.which === MouseKey.LEFT) {
                me.isPressed.leftButton = true;
            }
            if (event.which === MouseKey.RIGHT) {
                me.isPressed.rightButton = true;
            }
            if (event.which === MouseKey.MIDDLE) {
                me.isPressed.middleButton = true;
            }
        };

        // Mouse up event function definition
        canvas2D.onmouseup = function(event) {
            if(event.which === MouseKey.LEFT) {
                me.isPressed.leftButton = false;
                oi.selectedGameObjectAxis = null;
            }
            if (event.which === MouseKey.RIGHT) {
                me.isPressed.rightButton = false;
            }
            if (event.which === MouseKey.MIDDLE) {
                me.isPressed.middleButton = false;
            }
        };

        // Mouse leave event function definition
        canvas2D.onmouseleave = function(event) {
            if(me.isPressed.leftButton) {
                me.isPressed.leftButton = false;
            }
            if (me.isPressed.rightButton) {
                me.isPressed.rightButton = false;
            }
            if (me.isPressed.middleButton) {
                me.isPressed.middleButton = false;
            }
        };

        // Mouse move event function definition
        canvas2D.onmousemove  = function(event) {
            me.movementX = event.movementX;
            me.movementY = event.movementY;

            me.offsetX = event.offsetX;
            me.offsetY = event.offsetY;
            /*
            if (mouseInput.leftClickIsActive || mouseInput.rightClickIsActive) {
                mouseInput.moveX = event.movementX;
                mouseInput.moveY = event.movementY;
                mouseInput.moveGameObjectX = event.movementX;
                mouseInput.moveGameObjectY = event.movementY;
            }
            */
        };

        // Mouse wheel event function definition
        canvas2D.onwheel = function(event) {
            me.wheelDelta = event.deltaY;
        };
    }

    processCameraNavigation (camera) {
        // Zoom camera view in and out
        if (this.mouseEvent.isPressed.middleButton) {
            camera.translate([0.0, 0.0, -(this.mouseEvent.movementY+this.mouseEvent.movementX)/2]);
            this.mouseEvent.movementX = 0;
            this.mouseEvent.movementY = 0;
        } // Pan the camera view
        else if(this.mouseEvent.isPressed.leftButton) {
            camera.translate([-this.mouseEvent.movementX/4, this.mouseEvent.movementY/4, 0.0]);
            this.mouseEvent.movementX = 0;
            this.mouseEvent.movementY = 0;
        } // Rotate the camera view
        else if (this.mouseEvent.isPressed.rightButton) {
            camera.rotate([-this.mouseEvent.movementY/8, -this.mouseEvent.movementX/8, 0.0]);
            this.mouseEvent.movementX = 0;
            this.mouseEvent.movementY = 0;
        }

        if (Math.abs(this.mouseEvent.wheelDelta) > 0.01) {
            if (this.mouseEvent.wheelDelta > 0.0) {
                camera.translate([0.0, 0.0, 4.0]);
            }
            else
                camera.translate([0.0, 0.0, -4.0]);

            this.mouseEvent.wheelDelta = 0;
        }
    }

    processObjectInteraction () {
        if (!this.mouseEvent.isPressed.leftButton)
            return;

        console.log("test");
        // Extend the click box borders
        let offset = 4;

        for (let i = 0; i < this.sceneObjects.length; ++i) {
            // Check if the coordinate system is visible
            if (this.sceneObjects[i].localCoordinateSystemUI.isVisible) {
                // Check if mouse is in the click box area
                if (event.offsetX <= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.xMax + offset &&
                    event.offsetX >= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.xMin - offset &&
                    event.offsetY <= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.yMax + offset &&
                    event.offsetY >= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisX.borders.yMin - offset) {

                    this.objectInteraction.selectedGameObjectAxis = 'x';
                }
                else if (event.offsetX <= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMax + offset &&
                    event.offsetX >= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMin - offset &&
                    event.offsetY <= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.yMax + offset &&
                    event.offsetY >= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.yMin - offset) {

                    this.objectInteraction.selectedGameObjectAxis = 'y';
                }
                else if (event.offsetX <= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisY.borders.xMax + offset &&
                    event.offsetX >= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisZ.borders.xMin - offset &&
                    event.offsetY <= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisZ.borders.yMax + offset &&
                    event.offsetY >= this.sceneObjects[i].localCoordinateSystemUI.clickBoxAxisZ.borders.yMin - offset) {

                    this.objectInteraction.selectedGameObjectAxis = 'z';
                }

                this.objectInteraction.selectedGameObjectIndex = i;
            }
        }
    }
}

export default EventHandler;
