import React, { Component } from 'react';

// CSS
import './App.css';

// JS
import EngineWindow from './js/bluebox/gui/EngineWindow';
import SidePanel from './js/bluebox/gui/SidePanel';
import TopPanel from './js/bluebox/gui/TopPanel'

class App extends Component {

    render() {
        const engineWindowWidth = 1024;
        const engineWindowHeight = 768;

        return (
            <div className="App-container">
                {/*
                <header id="app-header" style={{width: '100%'}} className="App-header">
                    <h1 className="App-title">BlueBox Demo</h1>
                </header>
                */}
                <div style={{backgroundColor: 'black', width: '100%', height: '100%', display: 'flex', flexDirection: 'column'}}>
                    <TopPanel/>
                    <div style={{height: '100%', display: 'flex', flexDirection: 'row'}}>
                        <SidePanel width={engineWindowWidth/4}/>
                        <EngineWindow width={engineWindowWidth} height={engineWindowHeight}/>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
