class MouseEventHandler {
    constructor() {
        let mouseInput = this.mouseInputReader;
        let gameObjects = this.sceneObjects;

        let canvas2D = document.getElementById('text-canvas');
        canvas2D.addEventListener("mousedown", this.mouseDownEvent(event), false);
        canvas2D.addEventListener("mouseup", this.mouseUpEvent(event), false);
        canvas2D.addEventListener("mouseleave", this.mouseLeaveEvent(event), false);
        canvas2D.addEventListener("mousemove", this.mouseMove(event), false);
    }

    mouseDownEvent(event) {
        let mouseInput = this.mouseInputReader;
        
        switch(event.which) {
            case MouseKey.LEFT_CLICK:
                break;
            case MouseKey.RIGHT_CLICK:
                break;
            default:
                break;
        }
    }

    mouseUpEvent(event) {
        let mouseInput = this.mouseInputReader;

        switch(event.which) {
            case MouseKey.LEFT_CLICK:
                break;
            case MouseKey.RIGHT_CLICK:
                break;
            default:
                break;
        }
    }

