class Vector3 {

    constructor(coordinates) {

        this.Set(coordinates);
    }

    Set(coords) {

        this.x = coords[0];
        this.y = coords[1];
        this.z = coords[2];
        this.magnitude = Math.sqrt(this.x*this.x + this.y*this.y + this.z*this.z);
    }

    Clone() {
        return new Vector3([this.x, this.y, this.z]);
    }

    Inverted() {

        return new Vector3 ([-this.x, -this.y, -this.z]);
    }

    Normalized() {

        let out = new Vector3();

        // Set length of vector to 1
        if (this.magnitude > 0.001) {
            out.Set([
                this.x/this.magnitude,
                this.y/this.magnitude,
                this.z/this.magnitude
            ]);
        }
        else {
            out.Set([
                0.0,
                0.0,
                0.0
            ]);
        }

        return out;
    }

    static Distance(a, b) {
        return Math.sqrt(
            (b.x-a.x)*(b.x-a.x) +
            (b.y-a.y)*(b.y-a.y) +
            (b.z-a.z)*(b.z-a.z)
        );
    }

    static Add (A, B) {
        return new Vector3 ([
            A.x + B.x,
            A.y + B.y,
            A.z + B.z
        ]);
    }

    static Subtract (A, B) {
        return new Vector3 ([
            A.x - B.x,
            A.y - B.y,
            A.z - B.z
        ]);
    }

    static CrossProduct (A, B) {
        return new Vector3 ([
            (A.y*B.z - A.z*B.y),
            -(A.x*B.z - A.z*B.x),
            (A.x*B.y - A.y*B.x)
        ]);
    }

    static ScalarProduct (A, B) {
        return A.x*B.x + A.y*B.y + A.z*A.z;
    }

    static Up() {
        return new Vector3([0.0, 1.0, 0.0]);
    }

    static Down() {
        return new Vector3([0.0, -1.0, 0.0]);
    }

    static Forward() {
        return new Vector3([0.0, 0.0, 1.0]);
    }

    static Backwards () {
        return new Vector3([0.0, 0.0, -1.0]);
    }

    static Left() {
        return new Vector3([-1.0, 0.0, 0.0]);
    }

    static Right() {
        return new Vector3([1.0, 0.0, 0.0]);
    }
}

export default Vector3;
